const validate = value => {
  const errors = {};
  const { name, phone_number, address, date_of_birth, email, id_card, joining_date, official_date, contract_type, position, status, sex, code, password, fname } = value;
  if (!phone_number) {
    errors.phone_number = 'Please enter phone number';
  }
  if (!name) {
    errors.name = 'Please enter name';
  }
  else if(!/^[a-zA-Z]+$/.test(name)) {
    errors.name = 'Invalid name'
  }
  else if (name.length < 10 || name.length > 30) { 
    errors.name = 'Length of name must be from 10 to 30 characters';
  }
  if (!date_of_birth) {
    errors.date_of_birth = 'Please enter date of birth';
  }
  if (!id_card) {
    errors.id_card = 'Please enter id card';
  }
  else if (!/[0-9]/.test(id_card)) {
    errors.id_card = 'Id card must not contain characters'
  }
  else if (id_card.length < 9 || id_card.length > 12) {
    errors.id_card = 'Length of id card must be from 9 to 12 characters'
  }
  if (!joining_date) {
    errors.joining_date = 'Please enter joining date';
  }
  if (!official_date) {
    errors.official_date = 'Please enter official date';
  }
  if (!address) { 
    errors.address = 'Please enter address';
  }
  else if (address.length > 50) { 
    errors.address = 'Length of id card must be less than 50 characters';
  }
  if (!email) {
    errors.email = 'Please enter email';
  } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(email)) {
    errors.email = 'Invalid email address';
  } if (!contract_type) {
    errors.contract_type = 'Please enter contract type';
  }
  if (!position) {
    errors.position = 'Please enter position';
  }
  if (!status) {
    errors.status = 'Please enter status';
  }
  if (!sex) {
    errors.sex = 'Please enter sex';
  }
  if (!code) {
    errors.code = 'Please enter code';
  }
  if (!password) {
    errors.password = 'Please enter password';
  }
  if (!fname) {
    errors.fname = 'Please enter fname';
  }
  return errors;
};

export default validate;
