import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import { UncontrolledPopover } from 'reactstrap';
import { connect } from 'react-redux';
import * as actions from '../../actions/timeSheet';
import moment from 'moment'
import classNames from 'classnames'
import _ from 'lodash'
import DateRangePicker from '../../../commons/components/dateRangePicker'
import "react-dates/lib/css/_datepicker.css";
import { FaSortNumericDown, FaSortNumericUp } from 'react-icons/fa'
import { MdVisibility, MdVisibilityOff } from 'react-icons/md'
import './styles.css'
import './staffTimeSheet.scss'
import Time from '../../../commons/const/Time'
import OffSet from './offset'
import Pagination from '../../../commons/components/pagination'

const Timesheets = props => {
  const { timeSheet } = props;
  const [type, setType] = useState('All');
  const [modal, setModal] = useState(false);
  const [sortType, setSortType] = useState('ASC');
  const toggle = () => setModal(!modal);
  const [currentPage, setCurrentPage] = useState(1);
  const [date, setDate] = useState({
    startTime: moment().subtract(10, 'days').format(Time.DMY),
    endTime: moment().format(Time.DMY)
  })
  useEffect(() => {
    props.getStaffTimeSheet(date, currentPage, sortType, type);
    // eslint-disable-next-line
  }, [date, currentPage, type, sortType]);
  const letChangeSortType = () => setSortType(sortType === 'ASC' ? 'DESC' : 'ASC')
  //code show leave
  const [showLeave, setShowLeave] = useState(false);
  const refShowLeave = useRef();
  const clickOnLeaveList = () => {
    setShowLeave(true)
    document.addEventListener('click', clickOutSide)
  }
  const clickOutSide = (event) => {
    const { target } = event;
    if (refShowLeave.current !== null) {
      if (!refShowLeave.current.contains(target)) {
        setShowLeave(false)
        document.removeEventListener('click', clickOutSide)
      }
    }
  }
  useEffect(() => {
    return () => {
      if (showLeave === true) document.removeEventListener('click', clickOutSide)
    };
    // eslint-disable-next-line
  }, [showLeave])
  useEffect(() => {
    return () => document.removeEventListener('click', clickOutSide)
    // eslint-disable-next-line
  }, [])
  //code show leave
  return (
    <div>
      {timeSheet.data === undefined ? null
        :
        <main>
          {/* breadcrumb */}
          <div className="breadcrumb-wrapper">
            <div className="container-fluid">
              <nav id="breadcrumb-body" aria-label="breadcrumb">
                <ol className="breadcrumb">
                  <li className="breadcrumb-item"><Link to="/">Dashboard</Link></li>
                  <li className="breadcrumb-item active" aria-current="page">Timesheet</li>
                </ol>
              </nav>
            </div>
          </div>
          {/* breadcrumb end */}
          {/*tables-saffs  */}
          <div className="tables-saffs-wrapper">
            <div className="container-fluid">
              <div className="saffs-body">
                <div className="saffs-search">
                  <div className="times-select">
                    <DateRangePicker setDate={(data) => setDate(data)} />
                  </div>
                </div>
                <div className="tables-title">
                  <div className="tables-title-up">
                    {
                      _.map(['All', 'In late', 'Leave early', 'In late And Leave early'], (type, index) => (
                        <div className="tables-title-item" onClick={() => setType(type)} key={index}>{`${type}: ${timeSheet.statistic[_.snakeCase(type)]}`}</div>
                      ))
                    }
                    <div className="tables-title-item show-leave" id="PopoverLeave" onClick={clickOnLeaveList}>
                      Leave
                      {
                        showLeave === true ? <MdVisibility /> : <MdVisibilityOff />
                      }
                    </div>
                    {
                      _.map(['Total work', 'Total Off', 'Total Offset'], (type, index) => (
                        <div className="tables-title-item" key={index}>{`${type}: ${timeSheet.statistic[_.snakeCase(type)]}`}</div>
                      ))
                    }
                  </div>
                  <UncontrolledPopover trigger="legacy" placement="bottom" target="PopoverLeave">
                    <div className="tables-title-down popover-body" ref={refShowLeave}>
                      {
                        _.map(['Full day-off allowed and paid', 'Full day-off has allowed no paid', 'Full day-off no allowed', 'Half day-off allowed and paid', 'Half day-off has allowed no paid', 'Half day-off no allowed'], (type, index) => (
                          <div className="tables-title-item no-margin" onClick={() => setType(type)} key={index}>{`${type}: ${timeSheet.statistic[_.snakeCase(type)]}`}</div>
                        ))
                      }
                    </div>
                  </UncontrolledPopover>
                </div>
                {timeSheet.data.length === 0 ? null :
                  <div className="saffs-table-content">
                    <table className="table table-hover">
                      <thead>
                        <tr>
                          <th className='sort-date-timesheet'
                            onClick={letChangeSortType}
                          >
                            Date
                            {
                              sortType === 'ASC' ? <FaSortNumericUp className='icon-sort-date' /> : <FaSortNumericDown className='icon-sort-date' />
                            }
                          </th>
                          <th>CheckIn</th>
                          <th>CheckOut</th>
                          <th>Work</th>
                          <th>Off</th>
                          <th>Status</th>
                          <th />
                        </tr>
                      </thead>
                      <tbody>
                        {_.map(timeSheet.data, (item, index) => (
                          <tr key={index}>
                            <td>{item.date}</td>
                            <td
                              className={classNames('', {
                                red_text: moment(item.start_at, Time.HM).isAfter(moment(Time.startWork, Time.HM)) === true
                              })}
                            >
                              {item.start_at}
                            </td>
                            <td
                              className={classNames('', {
                                red_text: moment(item.end_at, Time.HM).isBefore(moment(Time.endWork, Time.HM)) === true
                              })}
                            >
                              {item.end_at}
                            </td>
                            <td
                              className={classNames('', {
                                red_text: item.time_work < 8
                              })}
                            >
                              {item.time_work}
                            </td>
                            <td
                              className={classNames('', {
                                red_text: item.time_off > 0
                              })}
                            >
                              {item.time_off}
                            </td>
                            <td
                              className={classNames('', {
                                red_text: item.status === 'unpaid leave',
                                green_text: item.status === 'paid leave'
                              })}
                            >
                              {item.status}
                            </td>
                            <td className="setting-button">
                              <div className='report-offset'>
                                <button type="button" className="btn btn-success">Report</button>
                                {
                                  item.time_work > 8 ?
                                    <div>
                                      < button
                                        type="button"
                                        className="btn btn-primary btn-offset"
                                        onClick={toggle}
                                      >
                                        OffSet
                                      </button>
                                      <OffSet dateSelected={item.date} modal={modal} toggle={() => toggle()} />
                                    </div>
                                    :
                                    null
                                }
                              </div>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                      <thead>
                        <tr>
                          <th className='sort-date-timesheet'
                            onClick={letChangeSortType}
                          >
                            Date
                            {
                              sortType === 'ASC' ? <FaSortNumericUp className='icon-sort-date' /> : <FaSortNumericDown className='icon-sort-date' />
                            }
                          </th>
                          <th>CheckIn</th>
                          <th>CheckOut</th>
                          <th>Work</th>
                          <th>Off</th>
                          <th>Status</th>
                          <th />
                        </tr>
                      </thead>
                    </table>
                  </div>
                }
                <Pagination totalPage={Number(timeSheet.statistic.total_page)} pageChange={(x) => setCurrentPage(x)} />
              </div>
            </div>
          </div>
          {/*tables-staffs end */}
        </main>
      }
    </div >
  )
}

const mapStatetoProps = (state) => {
  return {
    timeSheet: state.timeSheet,
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    getStaffTimeSheet: (date, current_page, sort_date, filter_type) => { dispatch(actions.getStaffTimeSheet({ date, current_page: current_page, sort_date: sort_date, filter_type: filter_type })) },
    offSet: (date, for_date) => { dispatch(actions.offSet({ date: date, for_date: for_date })) }
  }
}
export default connect(mapStatetoProps, mapDispatchToProps)(Timesheets);
