import React, { useState, useEffect } from "react";
import Time from "../../../../commons/const/Time";
import "../selectionStyle.css";
import { Link } from "react-router-dom/cjs/react-router-dom.min";
import moment from "moment";
import DatePicker from "../../../../commons/components/dateRangePicker";
import { Table, Badge } from "reactstrap";
import Pagination from "../../../../commons/components/pagination";
import ConfirmModal from "../../../../commons/components/modal/confirmModal";
import { useDispatch, useSelector } from "react-redux";
import {
  getListAccCompensation,
  setIsAllowedCompensation
} from "../../../actions/compensation";
import _ from "lodash";

const Compensation = () => {
  const [date, setDate] = useState({
    startTime: moment()
      .subtract(10, "days")
      .format(Time.DMY),
    endTime: moment().format(Time.DMY)
  });
  const [id, setID] = useState(null);
  const [current_page, setCurrentPage] = useState(1);
  const [modal, setModal] = useState(false);
  const toggle = () => setModal(!modal);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getListAccCompensation(date, id, current_page));
  }, [date, id, current_page]);
  const handleClick = (item) => {
    dispatch(setIsAllowedCompensation(id, item));
    setModal(!modal);
    window.location.reload(false);
  }
  const data = useSelector(state => state.getListAccCompensation.data);
  return (
    <main>
      <div className="breadcrumb-wrapper">
        <div className="container-fluid">
          <nav id="breadcrumb-body" aria-label="breadcrumb">
            <ol className="breadcrumb">
              <li className="breadcrumb-item">
                <Link to="/">Dashboard</Link>
              </li>
              <li className="breadcrumb-item active" aria-current="page">
                Compensation
              </li>
            </ol>
          </nav>
        </div>
      </div>
      <div className="date-picker">
        <DatePicker setDate={data => setDate(data)} />
      </div>
      <div>
        <Table className="table" hover>
          <thead>
            <tr>
              <td>Code</td>
              <td>Name</td>
              <td>Email</td>
              <td>Date</td>
              <td>For Date</td>
              <td>Start time</td>
              <td>End time</td>
              <td>Confirm</td>
            </tr>
          </thead>
          <tbody>
            {_.map(data, (item, i) => {
              return (
                <tr key={i++}>
                  <td>{item.code}</td>
                  <td>{item.name}</td>
                  <td>{item.email}</td>
                  <td>{item.date}</td>
                  <td>{item.for_date}</td>
                  <td>{item.start_at}</td>
                  <td>{item.end_at}</td>
                  <td onClick={() => setID(item.id)}>
                    {item.confirmation_status === "waiting" ? (
                      <ConfirmModal
                      isOpen={modal}
                      toggle={toggle}
                      item={data}
                      confirm={() => handleClick("confirm")}
                      refuse={() => handleClick("refuse")}
                    />
                    ) : (
                      item.confirmation_status === "confirm" ? 
                      <Badge color="success">Confirm</Badge>
                      : 
                      <Badge color="danger">Refuse</Badge>
                    )}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>
      </div>
      <div>
        <Pagination totalPage={66} pageChange={x => setCurrentPage(x)} />
      </div>
    </main>
  );
};
export default Compensation;
