import React, { useState } from "react";
import "react-dates/initialize";
import { DateRangePicker } from "react-dates";
import "react-dates/lib/css/_datepicker.css";
import moment from 'moment'
import { isInclusivelyBeforeDay } from 'react-dates';
import Time from '../../../commons/const/Time'

const DatePicker = props => {
  const { block, blockRange, setFieldValue } = props
  const [startDate, setStartDate] = useState(props.startDate);
  const [endDate, setEndDate] = useState(props.endDate);
  const [focusedInput, setFocusedInput] = useState(null);

  const handleDatesChange = ({ startDate, endDate }) => {
    setStartDate(startDate);
    setEndDate(endDate);
    if (startDate !== null && endDate !== null && focusedInput === "endDate") {
      setFieldValue('from_date', startDate.format(Time.crossDMY))
      setFieldValue('to_date', endDate.format(Time.crossDMY))
    }
  };

  const isOutsideRange = day => {
    if (blockRange === "left")
      return isInclusivelyBeforeDay(day, moment())
    else if (blockRange === "none")
      return false
    else
      return !isInclusivelyBeforeDay(day, moment())
  }

  return (
    <DateRangePicker
      startDate={startDate} // momentPropTypes.momentObj or null,
      startDateId="your_unique_start_date_id" // PropTypes.string.isRequired,
      endDate={endDate} // momentPropTypes.momentObj or null,
      endDateId="your_unique_end_date_id" // PropTypes.string.isRequired,
      focusedInput={focusedInput} // PropTypes.oneOf([START_DATE, END_DATE]) or null,
      onFocusChange={focusedInput => setFocusedInput(focusedInput)} // PropTypes.func.isRequired,
      onDatesChange={handleDatesChange}
      block={block}
      displayFormat={Time.crossDMY}
      minimumNights={0}
      isOutsideRange={day => isOutsideRange(day)}
    />
  );
}

export default DatePicker;
