import axios from "axios";
import FormData from 'form-data'
import { toastError, toastSuccess } from '../../commons/helpers/toastHelpers/'
import { hideLoading, showLoading } from './loading'
import _ from 'lodash'

export const getReport = (id, setInitialValues) => {
  const url = id !== null ? `/reports/${id}` : `/reports/new`
  axios.get(url)
    .then(res => {
      const { users_list, channels_list } = res.data
      const { report_message, attachments, send_to } = res.data.report_serializer
      setInitialValues({ report_message, attachments, users: users_list, channels: channels_list, send_to })
    })
    .catch(function (error) {
      toastError(error.response.data);
    })
}

export const postFile = (id, formData) => {
  formData.append("report_id", id)
  return dispatch => {
    dispatch(showLoading())
    axios({
      url: "/attachments",
      method: 'post',
      headers: { 'Content-Type': 'multipart/form-data' },
      data: formData
    })
      .then(res => {
        toastSuccess("Successful!")
      })
      .catch(function (error) {
        toastError(error.response.data);
      })
      .finally(function () {
        formData.delete('report_id')
        formData.delete('attachments[]')
        dispatch(hideLoading())
      });
  }
}

export const putFile = (id, formData) => {
  formData.append("report_id", id)
  return dispatch => {
    dispatch(showLoading())
    axios({
      url: "/attachments/update",
      method: 'post',
      headers: { 'Content-Type': 'multipart/form-data' },
      data: formData
    })
      .then(res => {
        toastSuccess("Successful!")
      })
      .catch(function (error) {
        toastError(error.response.data);
      })
      .finally(function () {
        formData.delete('report_id')
        formData.delete('delete_ids[]')
        formData.delete('attachments[]')
        dispatch(hideLoading())
      });
  }
}

export const postReport = (timesheet_id, formData, reload, data) => {
  const body = {
    timesheet_id,
    ...data
  }
  return dispatch => {
    dispatch(showLoading())
    axios.post(`/reports`, body)
      .then(res => {
        const { id } = res.data
        formData.get('attachments[]') !== null
          ? dispatch(postFile(id, formData))
          : toastSuccess("Successful!")
      })
      .catch(function (error) {
        toastError(error.response.data);
      })
      .finally(function () {
        // update report_id in timesheets
        reload()
        dispatch(hideLoading())
      });
  }
}

export const updateReport = (report_id, formData, data) => {
  const body = {
    ...data
  }
  return dispatch => {
    dispatch(showLoading())
    return axios.put(`/reports/${report_id}`, body)
      .then(res => {
        const { id, attachments } = res.data
        formData.get('attachments[]') !== null || formData.get('delete_ids[]') !== null
          ? _.isEqual(attachments, [])
            ? dispatch(postFile(id, formData))
            : dispatch(putFile(id, formData))
          : toastSuccess("Successful!")
      })
      .catch(function (error) {
        toastError(error.response.data);
      })
      .finally(function () {
        dispatch(hideLoading())
      });
  }
}
