import React, { useState, useEffect } from "react";
import "../selectionStyle.css";
import { Link } from "react-router-dom";
import DatePicker from "../../../../commons/components/dateRangePicker";
import { Table, Badge } from "reactstrap";
import Pagination from "../../../../commons/components/pagination";
import moment from "moment";
import Time from "../../../../commons/const/Time";
import { useDispatch, useSelector } from "react-redux";
import {
  getListAccOverTime,
  setIsAllowedOverTime
} from "../../../actions/overTime";
import _ from "lodash";
import ConfirmModal from "../../../../commons/components/modal/confirmModal";
import SearchUser from "../../../../commons/components/searchUser/searchUser";
import "bootstrap/dist/css/bootstrap.css";
import { TooltipItem } from "../../../../commons/components/toolTipItem/toolTipItem";

const OverTime = props => {
  const [date, setDate] = useState({
    startTime: moment()
      .subtract(10, "days")
      .format(Time.DMY),
    endTime: moment().format(Time.DMY)
  });
  const [id, setID] = useState(null);
  const [current_page, setCurrentPage] = useState(1);
  const [modal, setModal] = useState(false);
  const [idStaff, setIDStaff] = useState();
  const toggle = () => setModal(!modal);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getListAccOverTime(date, idStaff, current_page));
  }, [date, idStaff, current_page]);

  const listOvertime = useSelector(state => state.getListAccOverTime.data);
  const handleClick = item => {
    dispatch(setIsAllowedOverTime(id, item));
    setModal(!modal);
    window.location.reload(false);
  };
  return (
    <main>
      <div className="breadcrumb-wrapper">
        <div className="container-fluid">
          <nav id="breadcrumb-body" aria-label="breadcrumb">
            <ol className="breadcrumb">
              <li className="breadcrumb-item">
                <Link to="/">Dashboard</Link>
              </li>
              <li className="breadcrumb-item active" aria-current="page">
                Over Time
              </li>
            </ol>
          </nav>
        </div>
      </div>
      <div className="selections">
        <div className="date-picker">
          <DatePicker setDate={data => setDate(data)} />
        </div>
        <div className="search-user">
          <SearchUser onSelectStaff={d => setIDStaff(d)} />
        </div>
      </div>
      <div>
        <Table className="table-selections" hover>
          <thead>
            <tr>
              <td>Code</td>
              <td>Name</td>
              <td>Email</td>
              <td>Date</td>
              <td>Start time</td>
              <td>End time</td>
              <td>Reason</td>
              <td>Confirm</td>
            </tr>
          </thead>
          <tbody>
            {_.map(listOvertime.data, (item, i) => {
              return (
                <tr key={i++}>
                  <td>{item.code}</td>
                  <td>{item.name}</td>
                  <td>{item.email}</td>
                  <td>{item.date}</td>
                  <td>{item.start_at}</td>
                  <td>{item.end_at}</td>
                  {item.reason === null ? (
                    <td>
                      <Badge color="secondary">không phép</Badge>
                    </td>
                  ) : item.reason.length > 8 ? (
                    <td>
                      <TooltipItem item={item} />
                    </td>
                  ) : (
                    <td>{item.reason}</td>
                  )}
                  <td onClick={() => setID(item.id)}>
                    {item.confirmation_status === "waiting" ? (
                      <ConfirmModal
                        isOpen={modal}
                        toggle={toggle}
                        item={listOvertime.data}
                        confirm={() => handleClick("confirm")}
                        refuse={() => handleClick("refuse")}
                      />
                    ) : item.confirmation_status === "confirm" ? (
                      <Badge color="success">Confirm</Badge>
                    ) : (
                      <Badge color="danger">Refuse</Badge>
                    )}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>
      </div>
      <div className="pagination-selections">
        <Pagination
          totalPage={listOvertime.total_page}
          pageChange={x => setCurrentPage(x)}
        />
      </div>
    </main>
  );
};
export default OverTime;
