import React, {useState, useEffect} from 'react';
import './overTime.css';
import {Link} from 'react-router-dom';
import DatePicker from '../../../../commons/components/dateRangePicker';
import {Table, Badge, Modal, ModalHeader, ModalBody, ModalFooter, Button} from 'reactstrap';
import Pagination from '../../../../commons/components/pagination';
import moment from "moment";
import Time from "../../../../commons/const/Time";
import { useDispatch, useSelector } from 'react-redux';
import { getListAccOverTime, setIsAllowedOverTime } from '../../../actions/overTime';
import _ from 'lodash';

const OverTime = () => {
  const [date, setDate] = useState({
    startTime: moment().subtract(10, 'days').format(Time.DMY),
    endTime: moment().format(Time.DMY)
  });
  const [id, setID] = useState(null);
  const [current_page, setCurrentPage] = useState(1);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getListAccOverTime(date, id, current_page))
  }, [date, id, current_page]);
  const data = useSelector(state => state.getListAccOverTime.data);
  useEffect(() => {
    dispatch(setIsAllowedOverTime(id))
  }, [id]);
  const isAllowed = useSelector(state => state.setIsAllowedOverTime)
  const [modal, setModal] = useState(false);
  const toggle = () => setModal(!modal);
  const isConfirm = () => {
    _.map(data.data, e => ({...e, is_allowed: isAllowed}));
    setModal(!modal);
  }
  return (
    <main>
      <div className="breadcrumb-wrapper">
        <div className="container-fluid">
            <nav id="breadcrumb-body" aria-label="breadcrumb">
              <ol className="breadcrumb">
                <li className="breadcrumb-item">
                  <Link to="/">Dashboard</Link>
                </li>
                <li className="breadcrumb-item active" aria-current="page">
                  Over Time
                </li>
              </ol>
            </nav>
          </div>
      </div>
      <div className="date-picker">
        <DatePicker setDate={data => setDate(data)}/>
      </div>
      <div>
        <Table className="table" hover>
          <thead>
            <tr>
              <td>Code</td>
              <td>Name</td>
              <td>Email</td>
              <td>Date</td>
              <td>Start time</td>
              <td>End time</td>
              <td>Confirm</td>
            </tr>
          </thead>
          <tbody>
            {
              _.map(data.data, (item, i) => {
                return (
                  <tr key={i++}>
                    <td>{item.code}</td>
                    <td>{item.name}</td>
                    <td>{item.email}</td>
                    <td>{item.date}</td>
                    <td>{item.start_at}</td>
                    <td>{item.end_at}</td>
                    <td onClick={() => setID(i++)}>
                      {item.is_allowed ? <Badge color="success">Confirm</Badge> : <Badge href="#" color="warning" onClick={toggle}>Waiting</Badge>}
                    </td>
                  </tr>
                )
              })
            }
          </tbody>
        </Table>
      </div>
      <div>
        <Pagination totalPage={66} pageChange={x => setCurrentPage(x)} />
      </div>
      <div>
        <Modal isOpen={modal} toggle={toggle}>
          <ModalHeader toggle={toggle}>Confirm</ModalHeader>
          <ModalBody>
            <p>Bạn đã chắc chắn chưa ?</p>
          </ModalBody>
          <ModalFooter>
            <Button color="secondary" onClick={toggle}>Close</Button>
            <Button color="warning" onClick={isConfirm}>Save</Button>
          </ModalFooter>
        </Modal>
      </div>
    </main>
  )
}
export default OverTime;
