import React, { useState } from 'react'
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux';
import { Label, Row, Col, Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { Field, Form, Formik, ErrorMessage } from "formik";
import * as Yup from 'yup';
import { ReactstrapInput, ReactstrapSelect, ReactstrapRadio } from "reactstrap-formik";

import * as dayOffActions from '../../actions/dayOffs'
import * as options from './selectOptions'
import Time from '../../../commons/const/Time'
import DatePicker from '../../../commons/components/dateRangePicker'
import moment from 'moment';
import { validationSchema } from './validate';

function DayOffForm(props) {
  const {
    dayOffActions,
    buttonLabel,
    title,
    item
  } = props

  const [date, setDate] = useState(
    item ? {
      startTime: item.from_date,
      endTime: item.to_date
    } : {
        startTime: moment(),
        endTime: moment().add(1, 'day')
      })
  const [modal, setModal] = useState(false);
  const initialValues = item ? {
    date,
    status: item.status,
    reason: item.reason,
    is_paid: item.is_paid
  } : {
      date,
      status: '',
      reason: '',
      is_paid: false
    }
  const toggle = () => setModal(!modal);
  return (
    <div>
      <Button style={{ width: "100px" }} color="info" onClick={toggle}>{buttonLabel}</Button>
      <Modal isOpen={modal} toggle={toggle} centered={true}>
        <Formik
          initialValues={initialValues}
          validationSchema={validationSchema}
          onSubmit={(values, { setErrors, setSubmitting }) => {
            const data = {
              ...values,
              reason: values['reason'].trim(),
              from_date: moment(date.startTime, Time.DMY).format(Time.crossDMY),
              to_date: moment(date.endTime, Time.DMY).format(Time.crossDMY),
              request_date: moment().format(Time.crossDMY)
            }
            if (item === undefined) dayOffActions.addDayOffs(data, setErrors, setSubmitting, toggle)
            else dayOffActions.updateDayOffs(item.id, data, setErrors, setSubmitting, toggle)
          }}
        >
          {({ handleSubmit, isSubmitting, values, errors, touched }) => (
            < Form onSubmit={handleSubmit}>
              <ModalHeader toggle={toggle}>{title}</ModalHeader>
              <ModalBody>

                <Row>
                  <Col xs="12">
                    <Label for="date">Date</Label>
                    <DatePicker
                      name="date"
                      id="date"
                      blockRange="none"
                      block={true}
                      date={date}
                      setDate={setDate}
                    />
                    <ErrorMessage name="date">{msg => <div style={{ display: "block" }} className="invalid-feedback">{msg}</div>}</ErrorMessage>
                  </Col>
                </Row>

                <Row>
                  <Col>
                    <Field
                      label="Status"
                      name="status"
                      component={ReactstrapSelect}
                      inputprops={{
                        name: "status",
                        id: "status",
                        options: options.status,
                        defaultOption: "Select"
                      }}
                    />
                  </Col>
                  <Col>
                    <Label>Is paid</Label>
                    <Row className="is-paid" style={{ marginLeft: "0px" }}>
                      <Field
                        name="is_paid"
                        component={ReactstrapRadio}
                        value={Boolean(true)}
                        type="radio"
                        label="True"
                      />
                      <Field
                        name="is_paid"
                        component={ReactstrapRadio}
                        value={Boolean(false)}
                        type="radio"
                        label="False"
                      />
                    </Row>
                  </Col>
                </Row>
                <Row>
                  <Col xs="12">
                    <Field
                      label="Reason"
                      name="reason"
                      type="textarea"
                      component={ReactstrapInput}
                    />
                  </Col>
                </Row>
              </ModalBody>
              <ModalFooter>
                <Button color="primary" onClick={toggle}>Cancel</Button>{' '}
                <Button color="secondary" type="submit">Submit</Button>
              </ModalFooter>
            </Form>
          )}
        </Formik>
      </Modal>
    </div >
  )
}

const mapStateToProps = state => {
  return {

  };
};

const mapDispatchToProps = dispatch => {
  return {
    dayOffActions: bindActionCreators(dayOffActions, dispatch)
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(DayOffForm);
