import React, { useState } from "react";
import {
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Button,
  Badge
} from "reactstrap";

const ConfirmModal = props => {
  const { isOpen, toggle, item, confirm, refuse } = props;
  const isConfirm = () => {
    confirm(item);
    toggle;
  };
  const isRefuse = () => {
    refuse(item);
    toggle;
  };
  return (
    <div>
      <Badge color="warning" onClick={toggle}>
        Waiting
      </Badge>
      <Modal isOpen={isOpen} toggle={toggle} style={{marginTop: "190px"}}>
        <ModalHeader toggle={toggle}>Confirm</ModalHeader>
        <ModalBody>
          <p>Bạn đã chắc chắn chưa ?</p>
        </ModalBody>
        <ModalFooter>
          <Button color="success" onClick={() => isConfirm(item)}>
            Confirm
          </Button>
          <Button color="warning" onClick={() => isRefuse(item)}>
            Refuse
          </Button>
          <Button color="secondary" onClick={toggle}>
            Close
          </Button>
        </ModalFooter>
      </Modal>
    </div>
  );
};
export default ConfirmModal;
