import React, { useState } from "react";
import {
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  InputGroup,
  Input,
  InputGroupText,
  InputGroupAddon,
  Button
} from "reactstrap";
import "bootstrap/dist/css/bootstrap.css";
import { useDispatch } from "react-redux";
import { setUpdateTimeSheet } from "../../actions/timeSheet";

const UpdateModal = props => {
  const {id, time} = props;
  const [inputStart, setInputStart] = useState('');
  const [inputEnd, setInputEnd] = useState('');
  const [modal, setModal] = useState(false);
  const toggle = () => setModal(!modal);

  const dispatch = useDispatch();
  const handleClick = (start, end) => {
    dispatch(setUpdateTimeSheet(id, start, end));
    setModal(!modal);
    window.location.reload(false);
  }
  return (
    <div>
      <p onClick={toggle}>{time}</p>
      <Modal isOpen={modal} toggle={toggle} style={{ marginTop: "190px" }}>
        <ModalHeader toggle={toggle}>Update</ModalHeader>
        <ModalBody>
          <div className="container">
            <div className="row">
              <div className="col">
                <InputGroup>
                  <InputGroupAddon addonType="prepend">
                    <InputGroupText>Start at:</InputGroupText>
                  </InputGroupAddon>
                  <Input onChange={e => setInputStart(e.target.value)} />
                </InputGroup>
              </div>
              <div className="col">
                <InputGroup>
                  <InputGroupAddon addonType="prepend">
                    <InputGroupText>End at:</InputGroupText>
                  </InputGroupAddon>
                  <Input onChange={e => setInputEnd(e.target.value)} />
                </InputGroup>
              </div>
            </div>
          </div>
        </ModalBody>
        <ModalFooter>
          <Button color="success" onClick={() => handleClick(inputStart, inputEnd)}>Confirm</Button>
          <Button color="secondary" onClick={toggle}>
            Cancel
          </Button>
        </ModalFooter>
      </Modal>
    </div>
  );
};
export default UpdateModal;
