import React, { useState, useEffect, useCallback } from 'react';
import CKEditor from '@ckeditor/ckeditor5-react';
import { Input, Button, Modal, ModalHeader, ModalBody, ModalFooter, Label } from 'reactstrap';
import '../../../commons/components/report/build/ckeditor'
import { getReport, postReport, updateReport, postFile } from '../../actions/report'
import _ from 'lodash';
import { useDispatch } from 'react-redux';
import Select from 'react-select';
import FormData from 'form-data'
import * as Yup from 'yup'
import { Formik } from 'formik';

import { styles } from './styles';
import { editorConfig } from './config';
import Images from './Images'

let formData = new FormData();

const filterMention = (report_message, users, convertIdToName) => {
  _.map(users,
    user =>
      report_message = _.replace(report_message, new RegExp(convertIdToName ? user.user_id : "@" + user.user_name, "g"), convertIdToName ? "@" + user.user_name : user.user_id)
  )
  return report_message
}

const validationSchema = Yup.object({
  send_to: Yup.array().required("Sent to is a required field"),
  report_message: Yup.string().required("Report message is a required field")
})

export default function (props) {
  const [initialValues, setInitialValues] = useState({
    send_to: [],
    report_message: ''
  })
  const [modal, setModal] = useState(true);
  const { item, reload, setSelectedItem } = props;
  const dispatch = useDispatch();

  const toggle = () => {
    setModal(!modal)
    setSelectedItem(null)
  }

  useEffect(() => {
    getReport(item.report_id, setInitialValues)
  }, [])

  const filterChannelInSelectField = channels => _.map(channels, o => ({ value: o.channel_code, label: o.channel_name }))
  const filterSendToInSelectField = send_to => _.map(send_to, o => ({ value: o.channel_code, label: o.channel_name }))

  const handleSubmit = useCallback(
    (values) => {
      const send_to_channels = _.map(values.send_to, o => ({ channel_code: o.value, channel_name: o.label }))

      //filter send_to user to submit
      const report_message = filterMention(values.report_message, values.users, false)

      let ins = document.getElementById('uploadFile').files.length;
      for (var x = 0; x < ins; x++) {
        formData.append("attachments[]", document.getElementById('uploadFile').files[x])
      }

      const data = {
        report_message, 
        send_to: !_.isEqual(send_to_channels, initialValues.send_to) ? (send_to_channels) : null
      }

      _.isEqual(item.report_id, null)
        ? dispatch(postReport(item.id , formData, reload, data))
        : dispatch(updateReport(item.report_id, formData, data))
      toggle()
    }
  )

  return (
    <>
      {
        initialValues.attachments !== undefined && <div>
          <Modal size="lg" isOpen={modal} toggle={toggle}>
            <Formik
              initialValues={initialValues}
              validationSchema={validationSchema}
              onSubmit={handleSubmit}
            >
              {props => (
                <form onSubmit={props.handleSubmit}>
                  <ModalHeader toggle={toggle}>Daily Report!</ModalHeader>
                  <ModalBody>
                    <div>
                      <Label style={styles.label}>Send to:</Label>
                      <Select
                        name="send_to"
                        isMulti
                        defaultValue={filterSendToInSelectField(props.values.send_to)}
                        options={filterChannelInSelectField(props.values.channels)}
                        onChange={e => props.setFieldValue('send_to', e)}
                      />
                      {props.errors.send_to && <div style={{ display: "block" }} className="invalid-feedback">{props.errors.send_to}</div>}
                      <Label style={styles.label}>Report</Label>
                      <CKEditor
                        editor={ClassicEditor}
                        config={editorConfig(props.values.users)}
                        values={filterMention(props.values.report_message, props.values.users, true)}
                        data={filterMention(props.values.report_message, props.values.users, true)}
                        onChange={(event, editor) => {
                          props.setFieldValue('report_message', editor.getData())
                        }}
                      />
                      {props.errors.report_message && <div style={{ display: "block" }} className="invalid-feedback">{props.errors.report_message}</div>}
                      {!_.isEqual(props.values.attachments, []) && <Images images={props.values.attachments} formData={formData} />}
                      <div style={{ marginTop: "10px" }}>
                        <Input
                          id="uploadFile"
                          type="file"
                          multiple
                        />
                      </div>
                    </div>
                  </ModalBody>
                  <ModalFooter>
                    <Button color="primary" type="submit">Submit</Button>{' '}
                    <Button color="secondary" onClick={toggle}>Cancel</Button>
                  </ModalFooter>
                </form>
              )}
            </Formik>
          </Modal>
        </div >
      }
    </>
  );
}
