const validate = value => {
  const errors = {}
  // danh sách Field cần validate lấy theo thuộc tính name của từng Field muốn validate
  const { currentPassword, newPassword, passwordConfirmation } = value
  // validate cho currentPassword
  if (!currentPassword) {
    errors.currentPassword = 'vui lòng nhập mật khẩu' //in ra lỗi 
  } else if (currentPassword.trim().length < 6) {
    errors.currentPassword = 'mật khẩu phải từ 6 kí tự' //in ra lỗi 
  } else if (currentPassword.trim().length > 15) {
    errors.currentPassword = 'mật khẩu phải ít hơn 15 kí tự' //in ra lỗi 
  }
  // validate cho newPassword
  if (!newPassword) {
    errors.newPassword = 'vui lòng nhập mật khẩu' //in ra lỗi 
  } else if (newPassword.trim().length < 6) {
    errors.newPassword = 'mật khẩu phải từ 6 kí tự' //in ra lỗi 
  } else if (newPassword.trim().length > 15) {
    errors.newPassword = 'mật khẩu phải ít hơn 15 kí tự' //in ra lỗi 
  }
  // validate cho passwordConfirmation
  if (!passwordConfirmation) {
    errors.passwordConfirmation = 'vui lòng nhập mật khẩu' //in ra lỗi 
  } else if (passwordConfirmation.trim().length < 6) {
    errors.passwordConfirmation = 'mật khẩu phải từ 6 kí tự' //in ra lỗi 
  } else if (passwordConfirmation.trim().length > 15) {
    errors.passwordConfirmation = 'mật khẩu phải ít hơn 15 kí tự' //in ra lỗi 
  }
  else if (passwordConfirmation !== newPassword) {
    errors.passwordConfirmation = 'xác nhận mật khẩu mới không trùng khớp' //in ra lỗi 
  }
  return errors
}

export default validate
