import React, { useEffect, useState } from "react";
import "../selectionStyle.css";
import { Link } from "react-router-dom";
import DatePicker from "../../dateRangePicker/";
import Table from "reactstrap/lib/Table";
import { getListAccountsDayOff, setIsAllowedDayOff } from "../../../actions/dayOff";
import { useDispatch, useSelector } from "react-redux";
import _ from "lodash";
import moment from "moment";
import Time from "../../../../commons/const/Time";
import { Tooltip, Badge } from "reactstrap";
import Pagination from "../../../../commons/components/pagination";
import ConfirmModal from "../../../../commons/components/modal/confirmModal";
import SearchUser from "../../../../commons/components/searchUser/searchUser";
import { TooltipItem } from "../../../../commons/components/toolTipItem/toolTipItem";

const DayOff = () => {
  const reduxStartDate = useSelector(state => state.dateRange.startDate.format(Time.crossDMY))
  const reduxEndDate = useSelector(state => state.dateRange.endDate.format(Time.crossDMY))
  const [id, setID] = useState(null);
  const [current_page, setCurrentPage] = useState(1);
  const [idStaff, setIDStaff] = useState();

  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getListAccountsDayOff(reduxStartDate, reduxEndDate, idStaff, current_page));
  }, [reduxStartDate, reduxEndDate, idStaff, current_page]);

  const listDayOff = useSelector(state => state.getListAccountsDayOff.data);
  const [modal, setModal] = useState(false);
  const toggle = () => setModal(!modal);
  const handleClick = (item) => {
    dispatch(setIsAllowedDayOff(id, item));
    window.location.reload(false);
  }

  return (
    <main>
      <div className="breadcrumb-wrapper">
        <div className="container-fluid">
          <nav id="breadcrumb-body" aria-label="breadcrumb">
            <ol className="breadcrumb">
              <li className="breadcrumb-item">
                <Link to="/">Dashboard</Link>
              </li>
              <li className="breadcrumb-item active" aria-current="page">
                Day Off
              </li>
            </ol>
          </nav>
        </div>
      </div>
      <div className="selections">
        <div className="date-picker">
          <DatePicker
            blockRange="none"
            setDate={data => setDate(data)}
          />
        </div>
        <div className="search-user">
          <SearchUser onSelectStaff={d => setIDStaff(d)} />
        </div>
      </div>
      <div>
        <Table className="table-selections" hover>
          <thead>
            <tr>
              <td>Code</td>
              <td>Name</td>
              <td>Email</td>
              <td>Date</td>
              <td>Reason</td>
              <td>Status</td>
              <td>Confirm</td>
            </tr>
          </thead>
          <tbody>
            {_.map(listDayOff.data, (item, i) => {
              return (
                <tr key={i++}>
                  <td>{item.code}</td>
                  <td>{item.name}</td>
                  <td>{item.email}</td>
                  <td>{item.request_date}</td>
                  {(item.reason.length > 8) ?
                    <td>
                      <TooltipItem item={item} />
                    </td>
                    :
                    <td>{item.reason}</td>
                  }
                  <td>{item.status}</td>
                  <td onClick={() => setID(item.id)}>
                    {item.confirmation_status === "waiting" ? (
                      <ConfirmModal
                        isOpen={modal}
                        toggle={toggle}
                        item={listDayOff.data}
                        confirm={() => handleClick("confirm")}
                        refuse={() => handleClick("refuse")}
                      />
                    ) : (
                        item.confirmation_status === "confirm" ?
                          <Badge color="success">Confirm</Badge>
                          :
                          <Badge color="danger">Refuse</Badge>
                      )}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>
      </div>
      <div className="pagination-selections">
        <Pagination totalPage={listDayOff.total_page} pageChange={(x) => setCurrentPage(x)} />
      </div>
    </main>
  );
};

export default DayOff;
