import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import { Input, UncontrolledPopover } from "reactstrap";
import { connect, useDispatch, useSelector } from "react-redux";
import * as usersAction from "../../actions/account";
import * as actions from "../../actions/timeSheet";
import "./styles.css";
import _ from "lodash";
import moment from "moment";
import classNames from "classnames";
import DateRangePicker from "../dateRangePicker";
import { FaSortNumericDown, FaSortNumericUp } from "react-icons/fa";
import { MdVisibility, MdVisibilityOff } from "react-icons/md";
import Time from "../../../commons/const/Time";
import Pagination from "../../../commons/components/pagination";
import calculateTimeWork from "../../../commons/function/calculateTimeWork";
import calculateTimeOff from "../../../commons/function/calculateTimeOff";
import SortTarget from "../../../commons/components/sortTarget";
import SearchUser from "../../../commons/components/searchUser/searchUser";
import UpdateModal from "./updateModal";

function Timesheets(props) {
  // const { allUser, timeSheet } = props;
  const [id, setID] = useState(null);
  const reduxStartDate = useSelector(state => state.dateRange.startDate.format(Time.crossDMY))
  const reduxEndDate = useSelector(state => state.dateRange.endDate.format(Time.crossDMY))
  const [idStaff, setIDStaff] = useState();
  const [currentPage, setCurrentPage] = useState(1);
  const [sortType, setSortType] = useState("ASC");
  const [sortField, setSortField] = useState("");
  const [filterType, setFilterType] = useState("All");
  const [showLeave, setShowLeave] = useState(false);
  const refShowLeave = useRef();

  const dispatch = useDispatch();
  const letChangeSortType = () =>
    setSortType(sortType === "ASC" ? "DESC" : "ASC");

  useEffect(() => {
    dispatch(actions.getStaffTimeSheetByAdmin(reduxStartDate, reduxEndDate, idStaff, currentPage, sortType, sortField, filterType))
    // eslint-disable-next-line
  }, [reduxStartDate, reduxEndDate, idStaff, currentPage, sortType, sortField, filterType]);
  const timeSheet = useSelector(state => state.timeSheet)

  const clickOnLeaveList = () => {
    setShowLeave(true);
    document.addEventListener("click", clickOutSide);
  };
  const clickOutSide = event => {
    const { target } = event;
    if (refShowLeave.current !== null) {
      if (!refShowLeave.current.contains(target)) {
        setShowLeave(false);
        document.removeEventListener("click", clickOutSide);
      }
    }
  };

  return (
    <main>
      {/* breadcrumb */}
      <div className="breadcrumb-wrapper">
        <div className="container-fluid">
          <nav id="breadcrumb-body" aria-label="breadcrumb">
            <ol className="breadcrumb">
              <li className="breadcrumb-item">
                <Link to="/">Dashboard</Link>
              </li>
              <li className="breadcrumb-item active" aria-current="page">
                Timesheets
              </li>
            </ol>
          </nav>
        </div>
      </div>
      {/* breadcrumb end */}
      {/*tables-saffs  */}
      <div className="tables-saffs-wrapper">
        <div className="container-fluid">
          <div className="saffs-body">
            <div className="saffs-search">
              <div className="times-select">
                <DateRangePicker 
                  setDate={data => setDate(data)} 
                  blockRange="none"
                />
              </div>
              <div className="saffs-search-content">
                <SearchUser onSelectStaff={d => setIDStaff(d)} />
              </div>
            </div>
            {timeSheet.data !== undefined ? (
              <div>
                <div>
                  <div className="tables-title">
                    <div className="tables-title-up">
                      {_.map(
                        [
                          "All",
                          "In late",
                          "Leave early",
                          "In late And Leave early"
                        ],
                        (type, index) => (
                          <div
                            className="tables-title-item"
                            onClick={() => setFilterType(type)}
                            key={index}
                          >{`${type}: ${
                            timeSheet.statistic[_.snakeCase(type)]
                          }`}</div>
                        )
                      )}
                      <div className="tables-title-item show-leave">
                        <span id="PopoverLeave" onClick={clickOnLeaveList}>
                          Leave
                        </span>
                        {showLeave === true ? (
                          <MdVisibility />
                        ) : (
                          <MdVisibilityOff />
                        )}
                      </div>
                      {_.map(
                        ["Total work", "Total Off", "Total Offset"],
                        (type, index) => (
                          <div
                            className="tables-title-item"
                            key={index}
                          >{`${type}: ${
                            timeSheet.statistic[_.snakeCase(type)]
                          }`}</div>
                        )
                      )}
                    </div>
                    <UncontrolledPopover
                      trigger="legacy"
                      placement="bottom"
                      target="PopoverLeave"
                    >
                      <div
                        className="tables-title-down popover-body"
                        ref={refShowLeave}
                      >
                        {_.map(
                          [
                            "Full day-off has allowed and paid",
                            "Full day-off has allowed not paid",
                            "Full day-off not allowed",
                            "Half day-off has allowed and paid",
                            "Half day-off has allowed not paid",
                            "Half day-off not allowed"
                          ],
                          (type, index) => (
                            <div
                              className="tables-title-item no-margin"
                              onClick={() => setType(type)}
                              key={index}
                            >{`${type}: ${
                              timeSheet.statistic[_.snakeCase(type)]
                            }`}</div>
                          )
                        )}
                      </div>
                    </UncontrolledPopover>
                  </div>
                  <div id="scrollbar-style" className="saffs-table-content">
                    <table className="table table-hover">
                      <thead>
                        <tr>

                          {_.map(
                            ["Code", "Name", "Date", "CheckIn", "CheckOut", "Work", "Off"],
                            (item, index) => (
                              <SortTarget
                                key={index}
                                type={item}
                                sortType={sortType}
                                sortField={sortField}
                                letSort={letChangeSortType}
                              />
                            )
                          )}
                          <th>Status</th>
                          <th>Compensated to</th>
                          <th />
                        </tr>
                      </thead>
                      <tbody>
                        {_.map(timeSheet.data, (item, index) => (
                          <tr key={index}>
                            <td>{item.code}</td>
                            <td>{item.name}</td>
                            <td>{item.date}</td>
                            <td
                              className={classNames("", {
                                red_text:
                                  moment(item.start_at, Time.HM).isAfter(
                                    moment(Time.startWork, Time.HM)
                                  ) === true
                              })}
                              onClick={() => setID(item.id)}
                            >
                              <UpdateModal id={id} time={item.start_at} />
                            </td>
                            <td
                              className={classNames("", {
                                red_text:
                                  moment(item.end_at, Time.HM).isBefore(
                                    moment(Time.endWork, Time.HM)
                                  ) === true
                              })}
                              onClick={() => setID(item.id)}
                            >
                              <UpdateModal id={id} time={item.end_at} />
                            </td>
                            <td
                              className={classNames("", {
                                red_text: item.time_work < 8
                              })}
                            >
                              {calculateTimeWork(item.time_work)}
                            </td>
                            <td
                              className={classNames("", {
                                red_text: item.time_off > 0
                              })}
                            >
                              {calculateTimeOff(item.time_off)}
                            </td>
                            <td
                              className={classNames("", {
                                red_text: item.status === "unpaid leave",
                                green_text: item.status === "paid leave"
                              })}
                            >
                              {item.status}
                            </td>
                            <td>{item.compensated_to}</td>
                            <td className="setting-button">
                              <button type="button" className="btn btn-success">
                                Report
                              </button>
                            </td>
                          </tr>
                        ))}
                      </tbody>

                    </table>
                  </div>
                </div>
                <Pagination
                  totalPage={Number(timeSheet.statistic.total_page)}
                  pageChange={x => setCurrentPage(x)}
                />
              </div>
            ) : null}
          </div>
        </div>
      </div>
      {/*tables-saffs end */}
    </main>
  );
}

export default Timesheets;
