import axios from 'axios'
import moment from 'moment'
import { toastError, toastSuccess } from '../../commons/helpers/toastHelpers'
import { history } from '../../commons/helpers/history/history';
import Time from '../../commons/const/Time';

export const checkIn = () => {
  const body = {
    date: moment().format(Time.DMY),
    start_at: moment().format(Time.HMS),
  }
  return (dispatch) => {
    axios.post('/checkin', body)
      .then(function (res) {
        toastSuccess('Check in thành công')
        history.push('/timesheets')
      })
      .catch(function (error) {
        toastError(error)
      })
  }
}

export const checkOut = () => {
  const body = {
    date: moment().format(Time.DMY),
    end_at: moment().format(Time.HMS),
  }
  return (dispatch) => {
    axios.patch('/checkout', body)
      .then(function (res) {
        toastSuccess('Check out thành công')
        history.push('/timesheets')
      })
      .catch(function (error) {
        toastError(error)
      })
  }
}

export const reportInlateLeaveEarly = (data) => {
  const body = {
    date: moment().format(Time.Full),
    reason: data.reason,
  }
  return (dispatch) => {
    axios.post('/report_late_early', body)
      .then(function (res) {
        toastSuccess('Report Thành Công')
      })
      .catch(function (error) {
        toastError(error)
      })
  }
}
