// mock.onPost('/staff/offsets').reply(200);
// mock.onGet('admin/users/14').reply(200, fakeData.users[3]);
// mock.onPost('/users/14/change_password').reply(200);
// mock.onGet('/dayoffs').reply(200, fakeData.currentuserdayoff)
// mock.onGet('/overtimes').reply(200, fakeData.currentuserovertime)

export const getStaffTimeSheet = (data) => {
  const params = {
    from_date: data.date.startTime,
    to_date: data.date.endTime,
    current_page: String(data.current_page),
    sort_date: data.sort_date,
    filter_type: data.filter_type
  }
  return (dispatch) => {
    dispatch(showLoading())
    axios.get('/timesheets', { params: params })
      .then(function (res) {
        dispatch(setTimeSheet(res.data))
      })
      .catch(function (error) {
        console.log(error.data);
        toastError(error)
      })
      .finally(function () {
        dispatch(hideLoading())
      });
  }
}

export const offSet = (data) => {
  const body = {
    date: data.date,
    for_date: data.for_date
  }
  return (dispatch) => {
    dispatch(showLoading())
    axios.post('/offsets', body)
      .then(function (res) {
        toastSuccess('Offset thành công')
      })
      .catch(function (error) {
        toastError(error)
      })
      .finally(function () {
        dispatch(hideLoading())
      });
  }
}

export const setTimeSheet = (data) => { return { type: actionTypes.setTimeSheet, data: data } }
