import axios from 'axios';
import * as accountContans from '../const/account';
import { showLoading, hideLoading } from './loading';
import { toastError, toastSuccess } from '../../commons/helpers/toastHelpers/index';
import { history } from '../../commons/helpers/history/history'

// ---------------API LIST ACCOUNT----------------

export const fetchListAccount = data => {
  return {
    type: accountContans.FETCH_ACCOUNT,
    data
  };
};

//Request list account
export const fetchListAccountRequest = () => {
  return dispatch => {
    dispatch(showLoading());
    axios.get('/admin/users')
      .then(res => {
        dispatch(fetchListAccount(res.data));
      })
      .catch(error => {
        toastError(error);
      })
      .finally(
        dispatch(hideLoading())
      )
  };
};

//Request search account
export const searchAccountRequest = (text) => {
  return dispatch => {
    dispatch(showLoading());
    axios.get('/admin/users', {
      params: {
        name: text
      }
    })
      .then(res => {
        dispatch(fetchListAccount(res.data));
      })
      .catch(error => {
        toastError(error);
      })
      .finally(
        dispatch(hideLoading())
      )
  };
};

// ---------------API ADD ACCOUNT----------------

export const addAccount = data => {
  return {
    type: accountContans.ADD_ACCOUNT,
    data
  };
};

//Request add account
export const addNewAccountRequest = (data) => {
  const body = {
    email: data.email,
    joining_date: data.joining_date,
    id_card: data.id_card,
    official_date: data.official_date,
    contract_type: data.contract_type,
    position: data.position,
    password: data.password,
    status: data.status,
    profile_attributes: {
      name: data.name,
      phone_number: data.phone_number,
      address: data.address,
      sex: data.sex,
      date_of_birth: data.date_of_birth
    },
    roles_attributes: [
      {
        name: data.roles
      }
    ]
  };
  return dispatch => {
    return axios.post('/admin/users', body)
      .then(res => {
        dispatch(addAccount(res.data));
        toastSuccess('Successful!');
      })
      .catch(error => {
        toastError(error);
        toastSuccess('Fail!');
      })
      .finally(
        dispatch(hideLoading())
      )
  };
};

// ---------------API UPDATE ACCOUNT----------------

export const setAccountEditting = account => {
  return {
    type: accountContans.SET_ACCOUNT_EDITTING,
    account,
  };
};

export const updateAccount = data => {
  return {
    type: accountContans.UPDATE_ACCOUNT,
    data
  }
};

//Request update account
export const updateAccountRequest = (id, data) => {
  const body = {
    email: data.email,
    joining_date: data.joining_date,
    id_card: data.id_card,
    official_date: data.official_date,
    contract_type: data.contract_type,
    position: data.position,
    password: data.password,
    status: data.status,
    profile_attributes: {
      name: data.name,
      phone_number: data.phone_number,
      address: data.address,
      sex: data.sex,
      date_of_birth: data.date_of_birth
    },
    roles_attributes: [
      {
        name: data.roles
      }
    ]
  };
  return dispatch => {
    dispatch(showLoading());
    axios.put(`/admin/users/${id}`, body)
      .then(res => {
        dispatch(updateAccount(res.data));
        toastSuccess('Successful!');
      })
      .catch(error => {
        console.log(error);
        toastError('Fail!');
      })
      .finally(
        dispatch(hideLoading())
      )
  };
};

// ---------------API DELETE ACCOUNT----------------

export const deleteAccount = data => {
  return {
    type: accountContans.DELETE_ACCOUNT,
    data
  };
};

// delete account
export const deleteAccountRequest = (id) => {
  return dispatch => {
    dispatch(showLoading());
    axios.delete(`/admin/users/${id}`)
      .then(res => {
        dispatch(deleteAccount(res.data));
        toastSuccess('Successful!');
        history.push('/');
      })
      .catch(error => {
        toastError(error);
        toastSuccess('Fail!');
      })
      .finally(
        dispatch(hideLoading())
      )
  };
};

// ---------------API DETAIL ACCOUNT----------------

export const fetchAccountItem = data => {
  return {
    type: accountContans.DETAIL_ACCOUNT,
    data
  };
};

// get detailaccount
export const fetchAccountItemRequest = id => {
  return dispatch => {
    dispatch(showLoading());
    axios.get(`/admin/users/${id}`)
      .then(res => {
        dispatch(fetchAccountItem(res.data));
      })
      .catch(error => {
        toastError(error);
      })
      .finally(
        dispatch(hideLoading())
      )
  };
};
