import React, { useEffect, useState } from "react";
import "../selectionStyle.css";
import { Link } from "react-router-dom";
import DatePicker from "../../../../commons/components/dateRangePicker";
import Table from "reactstrap/lib/Table";
import { getListAccountsDayOff, setIsAllowedDayOff } from "../../../actions/dayOff";
import { useDispatch, useSelector } from "react-redux";
import _ from "lodash";
import moment from "moment";
import Time from "../../../../commons/const/Time";
import { Tooltip, Badge} from "reactstrap";
import Pagination from "../../../../commons/components/pagination";
import ConfirmModal from "../../../../commons/components/modal/confirmModal";

const DayOff = () => {
  const [request_date, setDate] = useState({
    startTime: moment().subtract(10, 'days').format(Time.DMY),
    endTime: moment().format(Time.DMY)
  })
  const [id, setID] = useState(null);
  const [current_page, setCurrentPage] = useState(1);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getListAccountsDayOff(request_date, id, current_page));
  }, [request_date, id, current_page]);

  const data = useSelector(state => state.getListAccountsDayOff.data);
  const [modal, setModal] = useState(false);
  const toggle = () => setModal(!modal);
  const handleClick = (item) => {
    dispatch(setIsAllowedDayOff(id, item));
    setModal(!modal);
    window.location.reload(false);
  }
  return (
    <main>
      <div className="breadcrumb-wrapper">
        <div className="container-fluid">
          <nav id="breadcrumb-body" aria-label="breadcrumb">
            <ol className="breadcrumb">
              <li className="breadcrumb-item">
                <Link to="/">Dashboard</Link>
              </li>
              <li className="breadcrumb-item active" aria-current="page">
                Day Off
              </li>
            </ol>
          </nav>
        </div>
      </div>
      <div className="date-picker">
        <DatePicker setDate={(data) => setDate(data)}/>
      </div>
      <div>
        <Table className="table" hover>
          <thead>
            <tr>
              <td>Code</td>
              <td>Name</td>
              <td>Email</td>
              <td>Date</td>
              <td>Reason</td>
              <td>Status</td>
              <td>Confirm</td>
            </tr>
          </thead>
          <tbody>
            {_.map(data.data, (item, i) => {
              return (
                <tr key={i++}>
                  <td>{item.code}</td>
                  <td>{item.name}</td>
                  <td>{item.email}</td>
                  <td>{item.request_date}</td>
                  {(item.reason.length > 8) ? 
                    <td>
                      <TooltipItem item={item} />
                    </td>
                    :
                    <td>{item.reason}</td>
                  }
                  <td>{item.status}</td>
                  <td onClick={() => setID(item.id)}>
                    {item.confirmation_status === "waiting" ? (
                      <ConfirmModal
                      isOpen={modal}
                      toggle={toggle}
                      item={data}
                      confirm={() => handleClick("confirm")}
                      refuse={() => handleClick("refuse")}
                    />
                    ) : (
                      item.confirmation_status === "confirm" ? 
                      <Badge color="success">Confirm</Badge>
                      : 
                      <Badge color="danger">Refuse</Badge>
                    )}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>
      </div>
      <div>
        <Pagination totalPage={66} pageChange={(x) => setCurrentPage(x)} />
      </div>

    </main>
  );
};

export default DayOff;

const TooltipItem = (props) => {
  const {item} = props;
  const [isOpenTool, setIsOpen] = useState(false);
  const openTooltip = () => setIsOpen(!isOpenTool);
  return (
    <span>
      <p id={item.code}>
        {item.reason.substring(0,8)}...
      </p>
      <Tooltip style={{backgroundColor: '#E0E0E0', color: 'black'}} placement="right" isOpen={isOpenTool} target={item.code} toggle={openTooltip}>
        {item.reason}
      </Tooltip>
    </span>
  )
}
