import * as ActionTypes from '../const/account';
import axios from 'axios';
import {showLoading, hideLoading} from './loading';
import { toastSuccess } from '../../commons/helpers/toastHelpers';
import { history } from '../../commons/helpers/history/history';

export const getListAccountsDayOff = (request_date, id, current_page) => {
  const params = {
    from_date: request_date.startTime,
    to_date: request_date.endTime,
    id: id,
    current_page: String(current_page)
  }
  return (dispatch) => {
    dispatch(showLoading())
    axios.get('/admin/user_dayoffs', {params: params})
      .then(res => {
        dispatch({type: ActionTypes.LIST_ACC_DAYOFF, data: res.data})
      })
      .catch(error => {
        console.log(error)
      })
      .finally(() => {
        dispatch(hideLoading())
      })
  }
}

export const setIsAllowedDayOff = (id, status) => {
  const body = {
    confirmation_status: String(status)
  }
  return (dispatch) => {
    dispatch(showLoading())
    axios.patch(`/admin/user_dayoffs/${id}`, body)
      .then(res => {
        dispatch({type: ActionTypes.SET_ALLOWED_DAYOFF, data: res.data});
        toastSuccess('Successful');
        history.push(`/admin/user_dayoffs`)
      })
      .catch(error => {
        console.log(error)
      })
      .finally(() => {
        dispatch(hideLoading())
      })
  }
}
