import React, { useState, useEffect } from 'react'
import { connect } from 'react-redux'
import { Link } from "react-router-dom";
import { Breadcrumb, BreadcrumbItem, Table, Badge } from 'reactstrap'
import moment from 'moment'
import "react-dates/lib/css/_datepicker.css";
import _ from 'lodash';

import Time from '../../../commons/const/Time';
import * as actions from '../../actions/overtimes'
import DateRangePicker from '../../../commons/components/dateRangePicker'
import Pagination from '../../../commons/components/pagination'
import OvertimesFormModal from './OvertimeForm'

const Overtimes = props => {
  const [date, setDate] = useState({
    startTime: moment().subtract(10, 'days').format(Time.crossDMY),
    endTime: moment().format(Time.crossDMY)
  })
  const [currentPage, setCurrentPage] = useState(1);

  const { listOvertimes, totalPage } = props

  useEffect(() => {
    props.getOvertimes(date, currentPage)
  }, [date, currentPage])

  return (
    <div className="container-fluid">
      <Breadcrumb>
        <BreadcrumbItem>
          <Link to="/">Home</Link>
        </BreadcrumbItem>
        <BreadcrumbItem active>Overtime</BreadcrumbItem>
      </Breadcrumb>
      <div className="row mb-3">
        <div>
          <OvertimesFormModal
            title="Overtimes"
            buttonLabel="Add"
          />
        </div>
        <div>
          <DateRangePicker
            blockRange="none"
            setDate={setDate}
          />
        </div>
      </div>
      <div>
        <Table hover>
          <thead>
            <tr>
              <th>Date</th>
              <th>Start</th>
              <th>End</th>
              <th>Reason</th>
              <th>Status</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {
              _.map(listOvertimes, (item, index) => {
                const statusColor = { Confirm: "success", Refuse: "danger", Waiting: "warning" }
                const expired = moment().diff(moment(item.date, Time.crossDMY), "days") > 0

                return (
                  <tr key={index}>
                    <td>{item.date}</td>
                    <td>{item.start_at}</td>
                    <td>{item.end_at}</td>
                    <td className="reason">{item.reason}</td>
                    <td><Badge href="#" color={statusColor[item.confirmation_status]}>{item.confirmation_status}</Badge></td>
                    <td style={{ width: "15%" }}>
                      {item.confirmation_status === "Waiting" && !expired &&
                        <OvertimesFormModal
                          title="Edit overtimes"
                          buttonLabel="Edit"
                          item={item}
                          id={item.id}
                        />
                      }
                    </td>
                  </tr>
                )
              })
            }
          </tbody>
        </Table>
      </div>
      <div>
        <Pagination totalPage={totalPage} pageChange={setCurrentPage} />
      </div>
    </div>
  )
}

const mapStateToProps = (state) => {
  return {
    listOvertimes: state.overtimes.data,
    totalPage: state.overtimes.total_page
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    getOvertimes: (date, currentPage) => dispatch(actions.getOvertimes({ date, currentPage }))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Overtimes);
