import axios from 'axios';
import actionTypes from '../../commons/const/actionTypes'
import { toastSuccess } from '../../commons/helpers/toastHelpers';
import { showLoading, hideLoading } from './loading';
import FormData from 'form-data';

//Get CurrentUser
export const getInfoCurrentUser = () => {
  return (dispatch) => {
    axios.get(`users/${current_user_id}`) //hard code
      .then(function (res) {
        dispatch(setCurrentUser(res.data))
      })
      .catch(function (error) {
        console.log(error)
      })
  }
}
export const changeAvatarUser = (avatar) => {
  let formData = new FormData();
  formData.append('avatar', avatar);
  // const body = {
  //   avatar: String(avatar)
  // };
  return dispatch => {
    dispatch(showLoading());
    axios({
      url: '/profile',
      method: 'PUT',
      headers: { 'Content-Type': 'multipart/form-data' },
      data: formData,
    })
      .then(res => {
        dispatch({type: actionTypes.CHANGE_AVATAR_USER, data: res.data });
        toastSuccess('Successful!');
      })
      .catch(er => {
        toastSuccess(`${er.response.data}`);
      })
      .finally(() => {
        dispatch(hideLoading());
      })
  }
}

export const setCurrentUser = (data, status) => { return { type: actionTypes.setCurrentUser, data: data, status: status } }
