import * as ActionTypes from '../const/account';
import axios from 'axios';
import {showLoading, hideLoading} from './loading';
import { toastSuccess } from '../../commons/helpers/toastHelpers';

export const getListAccountsDayOff = (date, id, current_page) => {
  const params = {
    from_date: date.startTime,
    to_date: date.endTime,
    id: id,
    current_page: String(current_page)
  }
  return (dispatch) => {
    dispatch(showLoading())
    axios.get('admin/user_dayoffs', params)
      .then(res => {
        dispatch({type: ActionTypes.LIST_ACC_DAYOFF, data: res.data})
      })
      .catch(error => {
        console.log(error)
      })
      .finally(() => {
        dispatch(hideLoading())
      })
  }
}

export const setIsAllowedDayOff = (id) => {
  return (dispatch) => {
    dispatch(showLoading())
    axios.patch(`/admin/user_dayoffs/${id}`)
      .then(res => {
        dispatch({type: ActionTypes.SET_ALLOWED_DAYOFF, data: res.data});
        toastSuccess('Successful');
      })
      .catch(error => {
        console.log(error)
      })
      .finally(() => {
        dispatch(hideLoading())
      })
  }
}
