import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import { Input, UncontrolledPopover } from 'reactstrap';
import { connect } from 'react-redux';
import * as usersAction from '../../actions/account';
import * as actions from '../../actions/timeSheet'
import './styles.css';
import _ from 'lodash'
import moment from 'moment'
import classNames from 'classnames'
import DateRangePicker from '../../../commons/components/dateRangePicker'
import { FaSortNumericDown, FaSortNumericUp } from 'react-icons/fa'
import { MdVisibility, MdVisibilityOff } from 'react-icons/md'
import Time from '../../../commons/const/Time'
import Pagination from '../../../commons/components/pagination'
import calculateTimeWork from '../../../commons/function/calculateTimeWork';
import calculateTimeOff from '../../../commons/function/calculateTimeOff';

function Timesheets(props) {
  const { allUser, timeSheet } = props
  const [id, setID] = useState(null);
  const [type, setType] = useState('All');
  const [currentPage, setCurrentPage] = useState(1);
  const [sortType, setSortType] = useState('ASC');
  const letChangeSortType = () => setSortType(sortType === 'ASC' ? 'DESC' : 'ASC')
  const [date, setDate] = useState({
    startTime: moment().subtract(10, 'days').format(Time.DMY),
    endTime: moment().format(Time.DMY)
  })
  useEffect(() => {
    props.getAllUser();
    // eslint-disable-next-line
  }, []);
  useEffect(() => {
    if (id !== null) {
      props.getStaffTimeSheetByAdmin(date, id, currentPage, sortType, type);
    }
    // eslint-disable-next-line
  }, [id, date, type, currentPage, sortType]);
  //code show list staff
  const [showListStaff, setShowListStaff] = useState(false);
  const refShowListStaff = useRef();
  const clickOnListStaffList = () => {
    setShowListStaff(true)
    document.addEventListener('click', clickOutSideList)
  }
  const clickOutSideList = (event) => {
    const { target } = event;
    if (refShowListStaff.current !== null) {
      if (!refShowListStaff.current.contains(target)) {
        setShowListStaff(false)
        document.removeEventListener('click', clickOutSideList)
      }
    }
  }
  useEffect(() => {
    return () => {
      if (showListStaff === true) {
        document.removeEventListener('click', clickOutSideList)
      }
    };
    // eslint-disable-next-line
  }, [showListStaff])
  useEffect(() => {
    return () => {
      document.removeEventListener('click', clickOutSideList)
    };
    // eslint-disable-next-line
  }, [])
  //code show list staff
  //code search
  const [inputValue, setInputValue] = useState('');
  const listStaff = _.filter(allUser, n => (_.includes(n.profile.name.toLowerCase(), inputValue.toLowerCase())))
  //code search
  //code show leave
  const [showLeave, setShowLeave] = useState(false);
  const refShowLeave = useRef();
  const clickOnLeaveList = () => {
    setShowLeave(true)
    document.addEventListener('click', clickOutSide)
  }
  const clickOutSide = (event) => {
    const { target } = event;
    if (refShowLeave.current !== null) {
      if (!refShowLeave.current.contains(target)) {
        setShowLeave(false)
        document.removeEventListener('click', clickOutSide)
      }
    }
  }
  useEffect(() => {
    return () => {
      if (showLeave === true) {
        document.removeEventListener('click', clickOutSide)
      }
    };
    // eslint-disable-next-line
  }, [showLeave])
  useEffect(() => {
    return () => {
      document.removeEventListener('click', clickOutSide)
    };
    // eslint-disable-next-line
  }, [])
  //code show leave
  return (
    <main>
      {/* breadcrumb */}
      <div className="breadcrumb-wrapper">
        <div className="container-fluid">
          <nav id="breadcrumb-body" aria-label="breadcrumb">
            <ol className="breadcrumb">
              <li className="breadcrumb-item"><Link to="/">Dashboard</Link></li>
              <li className="breadcrumb-item active" aria-current="page">Timeshesst</li>
            </ol>
          </nav>
        </div>
      </div>
      {/* breadcrumb end */}
      {/*tables-saffs  */}
      <div className="tables-saffs-wrapper">
        <div className="container-fluid">
          <div className="saffs-body">
            <div className="saffs-search">
              <div className="times-select">
                <DateRangePicker setDate={(data) => setDate(data)} />
              </div>
              <div className="saffs-search-content" >
                <div className='div-outside-input' ref={refShowListStaff} onClick={clickOnListStaffList}>
                  <Input className='input-search-staff' placeholder='Enter the name of the employee' onChange={(e) => setInputValue(e.target.value)} />
                </div>
                <div id="scrollbar-style" className={classNames('div-list-staff', {
                  hideThisDiv: showListStaff === false
                })}
                >
                  {
                    _.map(listStaff, (item, index) => (
                      <div className='div-each-staff' key={index} onClick={() => (setID(item.id))}>
                        <p>{item.profile.name}</p>
                        <p>{`${item.profile.date_of_birth}   ${item.position}`}</p>
                      </div>
                    ))
                  }
                </div>
              </div>
            </div>
            {timeSheet.data !== undefined ?
              <div>
                <div>
                  <div className="tables-title">
                    <div className="tables-title-up">
                      {
                        _.map(['All', 'In late', 'Leave early', 'In late And Leave early'], (type, index) => (
                          <div className="tables-title-item" onClick={() => setType(type)} key={index}>{`${type}: ${timeSheet.statistic[_.snakeCase(type)]}`}</div>
                        ))
                      }
                      <div className="tables-title-item show-leave" >
                        <span id="PopoverLeave" onClick={clickOnLeaveList}>Leave</span>
                        {
                          showLeave === true ? <MdVisibility /> : <MdVisibilityOff />
                        }
                      </div>
                      {
                        _.map(['Total work', 'Total Off', 'Total Offset'], (type, index) => (
                          <div className="tables-title-item" key={index}>{`${type}: ${timeSheet.statistic[_.snakeCase(type)]}`}</div>
                        ))
                      }
                    </div>
                    <UncontrolledPopover trigger="legacy" placement="bottom" target="PopoverLeave">
                      <div className="tables-title-down popover-body" ref={refShowLeave}>
                        {
                          _.map(['Full day-off has allowed and paid', 'Full day-off has allowed not paid', 'Full day-off not allowed', 'Half day-off has allowed and paid', 'Half day-off has allowed not paid', 'Half day-off not allowed'], (type, index) => (
                            <div className="tables-title-item no-margin" onClick={() => setType(type)} key={index}>{`${type}: ${timeSheet.statistic[_.snakeCase(type)]}`}</div>
                          ))
                        }
                      </div>
                    </UncontrolledPopover>
                  </div>
                  <div id="scrollbar-style" className="saffs-table-content">
                    <table className="table table-hover">
                      <thead>
                        <tr>
                          <th className='sort-date-timesheet' onClick={letChangeSortType} >
                            Date
                            {
                              sortType === 'ASC' ? <FaSortNumericUp className='icon-sort-date' /> : <FaSortNumericDown className='icon-sort-date' />
                            }
                          </th>
                          <th>CheckIn</th>
                          <th>CheckOut</th>
                          <th>Work</th>
                          <th>Off</th>
                          <th>Status</th>
                          <th />
                        </tr>
                      </thead>
                      <tbody>
                        {_.map(timeSheet.data, (item, index) => (
                          <tr key={index}>
                            <td>{item.date}</td>
                            <td
                              className={classNames('', {
                                red_text: moment(item.start_at, Time.HM).isAfter(moment(Time.startWork, Time.HM)) === true
                              })}
                            >
                              {item.start_at}
                            </td>
                            <td
                              className={classNames('', {
                                red_text: moment(item.end_at, Time.HM).isBefore(moment(Time.endWork, Time.HM)) === true
                              })}
                            >
                              {item.end_at}
                            </td>
                            <td
                              className={classNames('', {
                                red_text: item.time_work < 8
                              })}
                            >
                              {calculateTimeWork(item.time_work)}
                            </td>
                            <td
                              className={classNames('', {
                                red_text: item.time_off > 0
                              })}
                            >
                              {calculateTimeOff(item.time_off)}
                            </td>
                            <td
                              className={classNames('', {
                                red_text: item.status === 'unpaid leave',
                                green_text: item.status === 'paid leave'
                              })}
                            >
                              {item.status}
                            </td>
                            <td className="setting-button"><button type="button" className="btn btn-success">Report</button></td>
                          </tr>
                        ))}
                      </tbody>
                      <thead>
                        <tr>
                          <th className='sort-date-timesheet' onClick={letChangeSortType} >
                            Date
                            {
                              sortType === 'ASC' ? <FaSortNumericUp className='icon-sort-date' /> : <FaSortNumericDown className='icon-sort-date' />
                            }
                          </th>
                          <th>CheckIn</th>
                          <th>CheckOut</th>
                          <th>Work</th>
                          <th>Off</th>
                          <th>Status</th>
                          <th />
                        </tr>
                      </thead>
                    </table>
                  </div>
                </div>
                <Pagination totalPage={Number(timeSheet.statistic.total_page)} pageChange={(x) => setCurrentPage(x)} />
              </div>
              :
              null
            }
          </div>
        </div>
      </div>
      {/*tables-saffs end */}
    </main>
  )
}

const mapStatetoProps = (state) => {
  return {
    allUser: state.accountReducer.listAccount,
    timeSheet: state.timeSheet,
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    getAllUser: () => { dispatch(usersAction.fetchListAccountRequest()) },
    getStaffTimeSheetByAdmin: (date, id, current_page, sort_date, filter_type) => { dispatch(actions.getStaffTimeSheetByAdmin({ date, id: id, current_page: current_page, sort_date: sort_date, filter_type: filter_type })) },
  }
}
export default connect(mapStatetoProps, mapDispatchToProps)(Timesheets);
