import actionTypes from '../const/actionTypes';
import axios from 'axios'
import { hideLoading, showLoading } from './loading'
import { toastError, toastSuccess } from '../../commons/helpers/toastHelpers'

// --------------GET OVERTIMES LIST---------------
export const setOvertimes = (data) => {
  return {
    type: actionTypes.GET_OVERTIMES,
    data
  }
}

export const getOvertimes = (data) => {
  const params = {
    from_date: data.date.startTime,
    to_date: data.date.endTime,
    current_page: String(data.currentPage)
  }
  return (dispatch) => {
    dispatch(showLoading())
    axios.get('/overtimes', { params })
      .then(function (res) {
        dispatch(setOvertimes(res.data))
      })
      .catch(function (error) {
        console.log(error)
      })
      .finally(function () {
        dispatch(hideLoading())
      });
  }
}

// ---------------ADD OVERTIMES---------------
const setAddOvertimes = data => {
  return {
    type: actionTypes.ADD_OVERTIMES,
    data
  }
}
export const addOvertimes = (data) => {
  const body = {
    date: data.date,
    start_at: data.start_at,
    end_at: data.end_at
  }
  return dispatch => {
    dispatch(showLoading())
    axios.post('/overtimes', body)
      .then(function (res) {
        dispatch(setAddOvertimes(res.data))
        toastSuccess("Successful!")
      })
      .catch(function (error) {
        console.log(error)
        toastError("Fail!")
      })
      .finally(function () {
        dispatch(hideLoading())
      });
  }
}

// --------------- UPDATE OVERTIMES ----------------
const setUpdateOvertimes = data => {
  return {
    type: actionTypes.UPDATE_OVERTIMES,
    data
  }
}

export const updateOvertimes = (id, data) => {
  const body = {
    date: data.date,
    start_at: data.start_at,
    end_at: data.end_at
  }
  return dispatch => {
    axios.put(`/overtimes/${id}`, body)
      .then(function (res) {
        dispatch(setUpdateOvertimes(res.data))
        toastSuccess("Successful!")
      })
      .catch(function (error) {
        console.log(error)
        toastError("Fail!")
      })
      .finally(function () {
        dispatch(hideLoading())
      });
  }
}
