import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import { Breadcrumb, BreadcrumbItem, UncontrolledPopover } from "reactstrap";
import { connect } from "react-redux";
import * as actions from "../../actions/timeSheet";
import moment from "moment";
import classNames from "classnames";
import _ from "lodash";
import DateRangePicker from '../dateRangePicker';
import "react-dates/lib/css/_datepicker.css";
import { FaSortNumericDown, FaSortNumericUp } from "react-icons/fa";
import { MdVisibility, MdVisibilityOff } from "react-icons/md";
import { bindActionCreators } from 'redux';

import "./styles.css";
import "./staffTimeSheet.scss";
import Time from "../../../commons/const/Time";
import OffSet from "./offset";
import Pagination from "../../../commons/components/pagination";
import calculateTimeWork from "../../../commons/function/calculateTimeWork";
import calculateTimeOff from "../../../commons/function/calculateTimeOff";
import SortTarget from "../../../commons/components/sortTarget";
import CustomPicker from '../../../commons/components/dateRangePicker/customPicker'
import { pickDate } from '../../actions/dateRange'

const Timesheets = props => {
  const { reduxStartDate, reduxEndDate, timeSheet, actions } = props;
  const [type, setType] = useState("All");
  const [modal, setModal] = useState(false);
  const [sortType, setSortType] = useState("ASC");
  const [sortField, setSortField] = useState("date");
  const toggle = () => setModal(!modal);
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    actions.getStaffTimeSheet({ startTime: reduxStartDate, endTime: reduxEndDate, currentPage, sortType, sortField, type});
    // eslint-disable-next-line
  }, [reduxStartDate, reduxEndDate, currentPage, type, sortType, sortField]);

  const letSort = field => {
    setSortType(sortType === "ASC" ? "DESC" : "ASC");
    setSortField(field);
  };
  //code show leave
  const [showLeave, setShowLeave] = useState(false);
  const refShowLeave = useRef();
  const clickOnLeaveList = () => {
    setShowLeave(true);
    document.addEventListener("click", clickOutSide);
  };
  const clickOutSide = event => {
    const { target } = event;
    if (refShowLeave.current !== null) {
      if (!refShowLeave.current.contains(target)) {
        setShowLeave(false);
        document.removeEventListener("click", clickOutSide);
      }
    }
  };
  useEffect(() => {
    return () => {
      if (showLeave === true)
        document.removeEventListener("click", clickOutSide);
    };
    // eslint-disable-next-line
  }, [showLeave]);
  useEffect(() => {
    return () => document.removeEventListener("click", clickOutSide);
    // eslint-disable-next-line
  }, []);
  //code show leave
  return (
    <div>
      {timeSheet.data === undefined ? null : (
        <div className="container-fluid">
          {/* breadcrumb */}
          <Breadcrumb>
            <BreadcrumbItem>
              <Link to="/">Home</Link>
            </BreadcrumbItem>
            <BreadcrumbItem active>Timesheets</BreadcrumbItem>
          </Breadcrumb>
          {/* breadcrumb end */}
          {/*tables-saffs  */}
          <div className="tables-saffs-wrapper">
              <div className="saffs-search">
                <div className="times-select">
                  <DateRangePicker blockRange="none" />
                </div>
                <CustomPicker pickDate={pickDate} />
              </div>
              <div className="tables-title">
                <div className="tables-title-up">
                  {_.map(
                    [
                      "All",
                      "In late",
                      "Leave early",
                      "In late and leave early"
                    ],
                    (type, index) => (
                      <div
                        className="tables-title-item"
                        onClick={() => setType(type)}
                        key={index}
                      >{`${type}: ${
                        timeSheet.statistic[_.snakeCase(type)]
                        }`}</div>
                    )
                  )}
                  <div className="tables-title-item show-leave">
                    <span id="PopoverLeave" onClick={clickOnLeaveList}>
                      Leave
                      </span>
                    {showLeave === true ? (
                      <MdVisibility />
                    ) : (
                        <MdVisibilityOff />
                      )}
                  </div>
                  {_.map(
                    ["Total work", "Total Off", "Total Offset"],
                    (type, index) => (
                      <div
                        className="tables-title-item"
                        key={index}
                      >{`${type}: ${
                        timeSheet.statistic[_.snakeCase(type)]
                        }h`}</div>
                    )
                  )}
                </div>
                <UncontrolledPopover
                  trigger="legacy"
                  placement="bottom"
                  target="PopoverLeave"
                >
                  <div
                    className="tables-title-down popover-body"
                    ref={refShowLeave}
                  >
                    {_.map(
                      [
                        "Full day-off has allowed and paid",
                        "Full day-off has allowed not paid",
                        "Full day-off not allowed",
                        "Half day-off has allowed and paid",
                        "Half day-off has allowed not paid",
                        "Half day-off not allowed"
                      ],
                      (type, index) => (
                        <div
                          className="tables-title-item no-margin"
                          onClick={() => setType(type)}
                          key={index}
                        >{`${type}: ${
                          timeSheet.statistic[_.snakeCase(type)]
                          }`}</div>
                      )
                    )}
                  </div>
                </UncontrolledPopover>
              </div>
              {timeSheet.data.length === 0 ? null : (
                  <table className="table table-hover">
                    <thead>
                      <tr>
                        <SortTarget type="Date" value="Date" sortType={sortType} sortField={sortField} letSort={letSort} />
                        <th>CheckIn</th>
                        <th>CheckOut</th>
                        <th>Work</th>
                        <th>Off</th>
                        {/* <th>Status</th> */}
                        <th>Compensate to</th>
                        <th>Compensated by</th>
                        <th />
                      </tr>
                    </thead>
                    <tbody>
                      {_.map(timeSheet.data, (item, index) => (
                        <tr key={index}>
                          <td>{item.date}</td>
                          <td
                            className={classNames("", {
                              red_text:
                                moment(item.start_at, Time.HM).isAfter(
                                  moment(Time.startWork, Time.HM)
                                ) === true
                            })}
                          >
                            {item.start_at}
                          </td>
                          <td
                            className={classNames("", {
                              red_text:
                                moment(item.end_at, Time.HM).isBefore(
                                  moment(Time.endWork, Time.HM)
                                ) === true
                            })}
                          >
                            {item.end_at}
                          </td>
                          <td
                            className={classNames("", {
                              red_text: item.time_work < 8
                            })}
                          >
                            {calculateTimeWork(item.time_work)}
                          </td>
                          <td
                            className={classNames("", {
                              red_text: item.time_off > 0
                            })}
                          >
                            {calculateTimeOff(item.time_off)}
                          </td>
                          {/* <td
                            className={classNames("", {
                              red_text: item.status === "unpaid leave",
                              green_text: item.status === "paid leave"
                            })}
                          >
                            {item.status}
                          </td> */}
                          <td>{item.compensate_to}</td>
                          <td>{item.compensated_by}</td>
                          <td className="setting-button">
                            <div className="report-offset">
                              <button
                                type="button"
                                className="btn btn-success"
                              >
                                Report
                                </button>
                              {(item.time_work > 8 && item.compensate_to === null) ? (
                                <div>
                                  <button
                                    type="button"
                                    className="btn btn-primary btn-offset"
                                    onClick={toggle}
                                  >
                                    OffSet
                                    </button>
                                  <OffSet
                                    dateSelected={item.date}
                                    modal={modal}
                                    toggle={() => toggle()}
                                  />
                                </div>
                              ) : null}
                            </div>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
              )}
              <Pagination
                totalPage={Number(timeSheet.statistic.total_page)}
                pageChange={x => setCurrentPage(x)}
              />
            </div>
          {/*tables-staffs end */}
        </div>
      )}
    </div>
  );
};

const mapStatetoProps = state => {
  return {
    timeSheet: state.timeSheet,
    reduxStartDate: state.dateRange.startDate.format(Time.crossDMY),
    reduxEndDate: state.dateRange.endDate.format(Time.crossDMY)
  };
};
const mapDispatchToProps = dispatch => {
  return {
    actions: bindActionCreators(actions, dispatch),
    pickDate: () => dispatch(pickDate)
  };
};
export default connect(mapStatetoProps, mapDispatchToProps)(Timesheets);
