import React from 'react';
import { Field, reduxForm } from 'redux-form';
import { connect } from 'react-redux';
import { compose, bindActionCreators } from 'redux';
import renderTextField from './FormTextField';
import renderSelectField from './SelectField';
import validate from '../../../../commons/helpers/validate/index';
import * as accountActions from '../../../actions/account';

const FORM_NAME = 'TASK_MANAGEMENT';
function AccountForm(props) {
  const handleSubmitForm = data => {
    const { accountActions, initialValues } = props;
    const { addNewAccountRequest, updateAccountRequest } = accountActions;
    if (initialValues && initialValues.id) {
      updateAccountRequest(initialValues.id, data);
    } else {
      addNewAccountRequest(data);
    }
  }

  const { invalid, handleSubmit, submitting, toggle } = props;

  const { initialValues } = props;
  return (
    <form onSubmit={handleSubmit(handleSubmitForm)}>
      <div className="form-group row">
        <Field
          name={initialValues ? "profile.name" : "name"}
          component={renderTextField}
          label="Name"
        />
        <Field
          name={initialValues ? "profile.phone_number" : "phone_number"}
          component={renderTextField}
          label="Phone number"
        />
      </div>
      <div className="form-group row">
        <Field
          name={initialValues ? "profile.address" : "address"}
          component={renderTextField}
          label="Address"
        />
        <Field
          name={initialValues ? "profile.date_of_birth" : "date_of_birth"}
          type="date"
          component={renderTextField}
          label="Date of birth"
        />
      </div>
      <div className="form-group row">
        <Field
          name="email"
          type="text"
          component={renderTextField}
          label="Email"
        />
        <Field
          name="id_card"
          type="text"
          component={renderTextField}
          label="Id card"
        />
      </div>
      <div className="form-group row">
        <Field
          name="joining_date"
          type="date"
          component={renderTextField}
          label="Joining date"
        />
        <Field
          name="official_date"
          type="date"
          component={renderTextField}
          label="Official date"
        />
      </div>
      <div className="form-group row">
        <Field
          name="contract_type"
          component={renderSelectField}
          label="Contract type"
        >
          <option value="">---Select---</option>
          <option value="full time">Full time</option>
          <option value="part time">Part time</option>
        </Field>
        <Field
          name="position"
          component={renderSelectField}
          label="Position"
        >
          <option value="">---Select---</option>
          <option value="developer">Developer</option>
          <option value="tester">Tester</option>
        </Field>
      </div>
      <div className="form-group row">
        {
          initialValues ? (
            <Field
              name="status"
              component={renderSelectField}
              label="Status"
            >
              <option value={Boolean(true)}>Active</option>
              <option value={Boolean(false)} >Deactive</option>
            </Field>
          ) : ""
        }
        <Field
          name={initialValues ? "profile.sex" : "sex"}
          component={renderSelectField}
          label="Gender"
        >
          <option value="">---Select---</option>
          <option value="male">Male</option>
          <option value="female">Female</option>
        </Field>
        {
          initialValues ?
            (
              <Field
                name="code"
                type="text"
                component={renderTextField}
                label="Code"
              />
            ) : (
              <Field
                name="password"
                type="text"
                component={renderTextField}
                label="Password"
              />
            )
        }
        {
          initialValues ? ""
            : (
              <Field
                name="fname"
                component={renderSelectField}
                label="Roles"
              >
                <option value="">---Select---</option>
                <option value="staff">Staff</option>
                <option value="admin">Admin</option>
              </Field>
            )
        }
      </div>
      <hr />
      <div className="btn-wrapper">
        <button onClick={toggle} type="button" className="btn btn-secondary mr-2">Close</button>
        <button onClick={toggle} disabled={invalid || submitting} type="submit" className="btn btn-success">Save</button>
      </div>
    </form>
  )
}

const mapStateToProps = state => {
  return {
    initialValues: state.accountReducer.accountEditTing,
  };
};

const mapDispatchToProps = dispatch => {
  return {
    accountActions: bindActionCreators(accountActions, dispatch),
  };
};

const withConnect = connect(mapStateToProps, mapDispatchToProps);
const withReduxForm = reduxForm({
  form: FORM_NAME,
  validate
});

export default compose(
  withConnect,
  withReduxForm
)
  (AccountForm);
