import axios from 'axios'
import moment from 'moment'
import { toastError, toastSuccess } from '../../commons/helpers/toastHelpers'
import { history } from '../../commons/helpers/history/history';
import Time from '../../commons/const/Time';

export const checkIn = (data) => {
  const body = {
    date: moment().format(Time.DMY),
    start_at: moment().format(Time.HMS),
    reason: data.reason
  }
  return (dispatch) => {
    axios.post('/checkin', body)
      .then(function (res) {
        toastSuccess('Check in thành công')
        history.push('/timesheets')
      })
      .catch(function (error) {
        toastError(error)
      })
  }
}

export const checkOut = (data) => {
  const body = {
    date: moment().format(Time.DMY),
    end_at: moment().format(Time.HMS),
    reason: data.reason
  }
  return (dispatch) => {
    axios.patch('/checkout', body)
      .then(function (res) {
        toastSuccess('Check out thành công')
        history.push('/timesheets')
      })
      .catch(function (error) {
        toastError(error)
      })
  }
}
