import React, { useState } from 'react'
import { Col, Row } from 'reactstrap';
import _ from 'lodash'

const styles = {
  container: {
    border: "1px solid #c4c4c4",
    padding: '0 10px',
    marginTop: '10px'
  },
  wapper: {
    border: "1px dashed #999",
    margin: "5px"
  },
  alignCenter: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    flexDirection: "column"
  },
  alignRow: {
    margin: "10px 0"
  },
  image: {
    width: '100%'
  },
  remove: {
    position: 'absolute',
    color: '#c3b7b7',
    right: '20px',
    top: '5px',
    cursor: 'pointer'
  }
}

const Images = (props) => {
  const [images, setImages] = useState(props.images)
  const removeImage = (image, index) => {
    props.formData.append("delete_ids[]", image.id)
    setImages([
      ...images.slice(0, index),
      ...images.slice(index + 1)
    ])
  }

  return (
    <div style={styles.container}>
      <Row xs='6'>
        {
          _.map(images, (image, index) =>
            <div key={index} style={{ ...styles.alignCenter }} >
              <Col style={{ ...styles.alignCenter, ...styles.alignRow }}>
                <img style={styles.image} src={image.file_url.thumb.url} alt={image} />
                <span onClick={() => removeImage(image, index)}>
                  <i class="fa fa-times" aria-hidden="true" style={styles.remove} />
                </span>
              </Col>
            </div >
          )
        }
      </Row>
    </div>
  )
}

export default Images
