import * as ActionTypes from "../const/account";
import axios from "axios";
import { showLoading, hideLoading } from "./loading";
import { history } from "../../commons/helpers/history/history";
import { toastSuccess } from "../../commons/helpers/toastHelpers";

export const getListAccOverTime = (date, user_id, current_page) => {
  const params = {
    from_date: date.startTime,
    to_date: date.endTime,
    user_id: user_id,
    current_page: String(current_page)
  };
  return dispatch => {
    dispatch(showLoading());
    axios
      .get("/admin/user_overtimes", { params: params })
      .then(res => {
        dispatch({ type: ActionTypes.LIST_ACC_OVERTIME, data: res.data });
      })
      .catch(error => {
        console.log(error);
      })
      .finally(() => {
        dispatch(hideLoading());
      });
  };
};

export const setIsAllowedOverTime = (id, status) => {
  const body = {
    confirmation_status: String(status)
  };
  return dispatch => {
    dispatch(showLoading());
    axios
      .patch(`/admin/user_overtimes/${id}`, body)
      .then(res => {
        dispatch({ type: ActionTypes.SET_ALLOWED_OVERTIME, data: res.data });
        toastSuccess("Successful");
        history.push("/admin/user_overtimes");
      })
      .catch(error => {
        console.log(error);
      })
      .finally(() => {
        dispatch(hideLoading());
      });
  };
};
