import * as ActionTypes from '../const/account';
import axios from 'axios';
import {showLoading, hideLoading} from './loading';
import {toastSuccess, toastError} from '../../commons/helpers/toastHelpers';
import { history } from '../../commons/helpers/history/history';

export const getListAccCompensation = (date, id, current_page) => {
  const params = {
    from_date: date.startTime,
    to_date: date.endTime,
    id: id,
    current_page: String(current_page)
  }
  return dispatch => {
    dispatch(showLoading());
    axios.get('/admin/user_compensations', {params: params})
      .then(res => {
        dispatch({type: ActionTypes.LIST_ACC_COMPENSATION, data: res.data})
      })
      .catch(error => {
        console.log(error);
      })
      .finally(() => {
        dispatch(hideLoading())
      })
  }
}

export const setIsAllowedCompensation = (id, status) => {
  const body = {
    confirmation_status: String(status)
  }
  return (dispatch) => {
    dispatch(showLoading())
    axios.patch(`/admin/user_compensations/${id}`, body)
      .then(res => {
        // dispatch({type: ActionTypes.SET_ALLOWED_COMPENSATION, data: res.data})
        toastSuccess('Successful');
        history.push(`/admin/user_compensations`)
      })
      .catch(er => {
        console.log(er)
        toastError('khong lay duoc du lieu :(')
      })
      .finally(() => {
        dispatch(hideLoading())
      })
  }
}
