import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { Breadcrumb, BreadcrumbItem, Table, Badge } from 'reactstrap';
import '../../asset/css/account.css';
import '../../asset/css/breadcrumb.css'
import * as accountActions from '../../actions/account';
import IconDow from '../../asset/img/dow.png';
import IconUp from '../../asset/img/up.png';
import IconSearch from '../../asset/img/search.png';
import { history } from '../../../commons/helpers/history/history';
import { DeleteModal } from '../../../commons/components/modal/';
import EditModal from './Modal'

const ListAccounts = props => {
  const [ftext, setText] = useState('');

  //------------------LIST ACCOUNT--------------
  useEffect(() => {
    const { fetchListAccountRequest } = props.accountActions;
    fetchListAccountRequest();
    // eslint-disable-next-line
  }, [])

  //------------------SEARCH ACCOUNT--------------
  const isOnchange = e => {
    setText(e.target.value);
  }
  const sumbmitSearchAccount = e => {
    e.preventDefault();
    const { searchAccountRequest } = props.accountActions;
    searchAccountRequest(ftext);
  }

  //------------------DELETE ACCOUNT--------------
  const handleDeleteAccount = item => {
    const { deleteAccountRequest } = props.accountActions;
    deleteAccountRequest(item.id);
  }

  //------------------ONCLICK DETAIL ACCOUNT--------------

  const onClickDetailAccount = (id) => {
    history.push(`/users/${id}`);
  }

  const { listAccount } = props;
  return (
    <div className="container-fluid p-0">
      <Breadcrumb style={{ padding: "0 20px" }}>
        <BreadcrumbItem><Link to="#">Home</Link></BreadcrumbItem>
        <BreadcrumbItem active>Accounts</BreadcrumbItem>
      </Breadcrumb>
      <div className="main-wrap">
        <div className="saffs-search">
          <EditModal
            type="add"
            buttonLabel="Add"
            title="Add new account"
          />
          <div className="saffs-search-content">
            <form onSubmit={sumbmitSearchAccount} className="form-inline">
              <input name="ftext" onChange={isOnchange} className="form-control mr-sm-2" type="search" placeholder="input name or email" aria-label="Search" />
              <button className="btn btn-search my-2 my-sm-0" type="submit"><img src={IconSearch}></img></button>
            </form>
          </div>
        </div>
        <div className="table-content">
          <Table hover>
            <thead>
              <tr>
                <th className="table-sort">
                  <div className="mt-2">Staff ID</div>
                  <div className="sort-icon ml-2">
                    <div className="icon-up">
                      <img src={IconUp} className="icon-dow-up" alt="icon_dow"></img>
                    </div>
                    <div className="icon-dow">
                      <img src={IconDow} className="icon-dow-up" alt="icon_up"></img>
                    </div>
                  </div>
                </th>
                <th>Name</th>
                <th>Email</th>
                <th>Role</th>
                <th>Status</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {
                listAccount.map((value, key) => {
                  return (
                    <tr key={key}>
                      <th onClick={() => onClickDetailAccount(value.id)} scope="row">{value.code}</th>
                      <td onClick={() => onClickDetailAccount(value.id)}>{value.profile.name}</td>
                      <td>{value.email}</td>
                      <td>{value.roles[0].name}</td>
                      <td>
                        {
                          value.status
                            ? <Badge href="#" color="success">Active</Badge>
                            : <Badge href="#" color="danger">Deactive</Badge>
                        }
                      </td>
                      <td className="setting-button">
                        <EditModal
                          buttonLabel="Edit"
                          title="Edit account"
                          account={value}
                        />
                        <DeleteModal item={value} deleteAccount={handleDeleteAccount} />
                      </td>
                    </tr>
                  )
                })
              }
            </tbody>
          </Table>
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    listAccount: state.accountReducer.listAccount,
  }
};

const mapDispatchToProps = (dispatch) => {
  return {
    accountActions: bindActionCreators(accountActions, dispatch),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ListAccounts);
