import * as ActionTypes from '../const/account';

let initialState = {
  data : [],
  confirmation_status: {}
};

const dayOffReducer = (state = initialState, action) => {
  switch (action.type) {
    case ActionTypes.LIST_ACC_DAYOFF:
      return {
        ...state,
        data: action.data
      };
    case ActionTypes.SET_ALLOWED_DAYOFF:
      return {
        ...state,
        confirmation_status: action.data
      }
    default:
      return state;
  }
}
export default dayOffReducer;
