import React, { useState, useEffect } from 'react';
import './Profile.css';
import Avatar from '../../../commons/assets/img/Avatar.png'
import General from './general/index'
import Connection from './connection/index'
import ChangePassword from './changePassword/index'
import { connect } from 'react-redux';
import * as actions from '../../actions/account';
import ChangeAvatar from './changeAvatar/modalChangeAvatar';
import _ from 'lodash';

function Profile(props) {
  const [profile, setProfile] = useState('General');
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => setIsOpen(!isOpen);

  const { currentUser, getInfoCurrentUser } = props

  useEffect(() => {
    getInfoCurrentUser();
    // eslint-disable-next-line
  }, []);

  return (
    <div>
      {
        currentUser.profile !== undefined ?
          < div className="profile" >
            <div className='profile-left'>
              <div className='img-name-id'>
                <img className='img' src={
                  _.isEqual(currentUser.providers, [])
                    ? Avatar
                    : "https://imp-storage-img-report.s3.ap-southeast-1.amazonaws.com/uploads/attachment/file_url/113/water-falls-1009136.jpg?response-content-disposition=inline&X-Amz-Security-Token=IQoJb3JpZ2luX2VjENf%2F%2F%2F%2F%2F%2F%2F%2F%2F%2FwEaDmFwLXNvdXRoZWFzdC0xIkgwRgIhAO2Jx%2B9KarLW5NAFyN9sKzEv4MmBvXAGOzlJ3i3dgU8%2FAiEA8zdMzLJySZyFSuvDWhkF%2BHtCiBB90QU7AvJhIT6xn%2Boq4wIIoP%2F%2F%2F%2F%2F%2F%2F%2F%2F%2FARAAGgwyMzUxMTE4NDUxNTAiDE%2Fq5pd4CVfoJFYelCq3Aka%2F%2F1hlaYBNR0KzabcrfFdVKJRaIWDyvaiDNZry7NBPlX1PHunGipXxX03FL7Ko%2BkXF2WzuE81tPj4Be2aFmYK3Ht1qCxr%2BTdCkrBlCduEJwD%2FBXoiidWF%2BTXgWceFpnQSoOcgl%2BD1peXMrcfjSqGr99wcf08PJ2kPgWj4AZ8%2B3LV4wKR2NEj1O3Ymh8inm5ezzeFZv6JMdjBBMWVLJX8kMXAGPwPpMq%2FoRrXkWSJBV4goN0sjWI9k%2BWcj3TJrQRurEc6A7jqqQIhVaO5yqIUBZI51l0m%2BHn5dSwkKlsouEQYEjZ95I%2Br9439qHL%2B6d%2B61OeVF8exmxsR8au%2B%2FELI6MfgSDvu1Je86xugO%2F1llALO7HGlTXubfP1IkME%2Fs6sXaoz%2BNY8VcE4OXRCL89T0itIgedSdTkMNCBpPkFOq8CxP%2BVbLHnxf1PjFoKHT8jSeo0oohNTr5g8qPxGx03G5jbpcGPJ2dbOuo1acH6B3Qv5RiVMCxEpQaWUx0tsO6eBIsxclcDgQvPnt3XOl7ELHLNwNchzNo021qBY5FWLLcTIqMd7977jkI7jAmc9glE%2FoT%2BCvKWeIiHQh2iXTzgKpGMQuZiF61Up75QL%2FTcYwizR%2BvBRg9Gn4aV0FzFmmdjAodCQ9rCWnoY%2BNLlfdAaZ2mXULr7MOUNlkOyqyFeGpfAXUEGoSL8aa15KdJjRR4zhKUvUWWvGVoKuB4dWGE3ZhxQGS6kQrX%2FbipDFk16fkDaaWV5qY67idnOT5rVbpJEFWm9nVWvQPX6V%2Bsg3fLLfbUntIf1JcsKWsRO5MowRdClzixjefudJJf57405APZn&X-Amz-Algorithm=AWS4-HMAC-SHA256&X-Amz-Date=20200804T070819Z&X-Amz-SignedHeaders=host&X-Amz-Expires=300&X-Amz-Credential=ASIATNPOCBUPLK6PEBN4%2F20200804%2Fap-southeast-1%2Fs3%2Faws4_request&X-Amz-Signature=c551b628d931886512d5f0859f804fb5484039ce140922008fd4e567660ba30b"
                } alt='avartar-img'
                  onClick={() => toggle()}
                />
                <div className='name-id'>
                  <p>
                    {_.isEqual(currentUser.providers, [])
                      ? currentUser.profile.name
                      : currentUser.providers[currentUser.providers.length - 1].name
                    }
                  </p>
                  <p>{currentUser.code}</p>
                </div>
              </div>
              <div className='menu-profile-left'>
                {
                  _.map(['General', 'Connection', 'Change Password'], (type, index) => (
                    <div className='menu-profile-left-child' onClick={() => setProfile(type)} key={index}>{type}</div>
                  ))
                }
              </div>
            </div>
            <div className='profile-right'>
              {
                profile === 'General' ?
                  <General getInfoCurrentUser={getInfoCurrentUser} currentUser={currentUser} />
                  :
                  profile === 'Connection' ?
                    <Connection currentUser={currentUser} />
                    :
                    profile === 'Change Password' ?
                      <ChangePassword getBack={() => setProfile('General')} />
                      :
                      null
              }
            </div>
          </div >
          : null
      }
      {isOpen && <ChangeAvatar modal={isOpen} toggle={toggle} />}
    </div>
  );
}
const mapStatetoProps = (state) => {
  return {
    currentUser: state.currentUser,
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    getInfoCurrentUser: () => { dispatch(actions.getInfoCurrentUser()) }
  }
}
export default connect(mapStatetoProps, mapDispatchToProps)(Profile);
