import React, { useState, useEffect } from 'react'
import { connect } from 'react-redux'
import { Table, Badge } from 'reactstrap'
import moment from 'moment'
import "react-dates/lib/css/_datepicker.css";
import _ from 'lodash';
import { bindActionCreators } from 'redux';

import Time from '../../../commons/const/Time';
import * as actions from '../../actions/dayOffs'
import DateRangePicker from '../../../commons/components/dateRangePicker'
import DayOffForm from './DayOffForm'
import Pagination from '../../../commons/components/pagination'

const DayOffs = props => {
  const [date, setDate] = useState({
    startTime: moment().subtract(10, 'days').format(Time.DMY),
    endTime: moment().format(Time.DMY)
  })
  const { listDayoff, totalPage, actions } = props
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    actions.getStaffDayOff({ startTime: date.startTime, endTime: date.endTime, currentPage })
  }, [date, currentPage])

  return (
    <div className="container-fluid" style={{ width: "95%" }}>
      <div className="row mx-0 mt-5 mb-3" style={{ verticalAlign: "none" }}>
        <div>
          <DayOffForm
            title="Dayoff"
            buttonLabel="Add"
          />
        </div>
        <div>
          <DateRangePicker
            blockRange="none"
            setDate={setDate}
          />
        </div>
      </div>
      <div>
        <Table hover>
          <thead>
            <tr>
              <th>From date</th>
              <th>To date</th>
              <th>Reason</th>
              <th>Status</th>
              <th>Confirm</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {
              _.map(listDayoff, (item, index) => {
                const statusColor = { confirm: "success", refuse: "danger", waiting: "warning" }
                const expired = moment().diff(moment(item.to_date, Time.crossDMY), "days") > 0

                return <tr key={index} style={{ height: "62px", verticalAlign: "none" }}>
                  <td>{moment(item.from_date, Time.DMY).format(Time.crossDMY)}</td>
                  <td>{moment(item.to_date, Time.DMY).format(Time.crossDMY)}</td>
                  <td>{item.reason}</td>
                  <td>{item.status}</td>
                  <td><Badge href="#" color={statusColor[item.confirmation_status]}>{item.confirmation_status}</Badge></td>
                  <td style={{ width: "15%" }}>
                    {item.confirmation_status === "waiting" && !expired &&
                      <DayOffForm
                        title="Edit dayoff"
                        buttonLabel="Edit"
                        item={item}
                      />
                    }
                  </td>
                </tr>
              }
              )
            }
          </tbody>
        </Table>
      </div>
      <div>
        <Pagination totalPage={totalPage} pageChange={setCurrentPage} />
      </div>
    </div>
  )
}

const mapStateToProps = (state) => {
  return {
    listDayoff: state.dayOffs.data,
    totalPage: state.dayOffs.total_page
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    actions: bindActionCreators(actions, dispatch)
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(DayOffs);
