// config CKEditor field
import _ from 'lodash';

const filterData = (data, field) => _.map(data, o => field === "user_name" ? ({ id: "@"+o[`${field}`] }) : ({ id: o[`${field}`] }))

export const mentionConfig = channels => ({
  toolbar: {
    items: []
  },
  mention: {
    feeds: [
      {
        marker: '#',
        feed: filterData(channels, "channel_name"),
        minimumCharacters: 0
      }
    ]
  }
})

export const editorConfig = users => ({
  toolbar: {
    items: [
      '|',
      'bold',
      'italic',
      'strikethrough',
      'code',
      'link',
      'bulletedList',
      'numberedList',
      '|',
      'undo',
      'redo',
      '|'
    ]
  },
  mention: {
    feeds: [
      {
        marker: '@',
        feed: filterData(users, "user_name"),
        minimumCharacters: 0
      },
    ]
  }
})
