import actionTypes from '../const/actionTypes';
import axios from 'axios'
import { hideLoading, showLoading } from './loading'
import { toastError, toastSuccess } from '../../commons/helpers/toastHelpers'

// --------------GET DAYOFF LIST---------------
export const setDayOffs = (data) => {
  return {
    type: actionTypes.GET_DAYOFF,
    data
  }
}

export const getStaffDayOff = (data) => {
  const params = {
    from_date: data.date.startTime,
    to_date: data.date.endTime,
    current_page: String(data.current_page)
  }
  return (dispatch) => {
    dispatch(showLoading())
    axios.get('/dayoffs', params)
      .then(function (res) {
        dispatch(setDayOffs(res.data))
      })
      .catch(function (error) {
        console.log(error)
      })
      .finally(function () {
        dispatch(hideLoading())
      });
  }
}

// ---------------ADD DAYOFF---------------
const addDayOffs = data => {
  return {
    type: ADD_DAYOFF,
    data
  }
}
export const addStaffDayOff = (data) => {
  const params = {
    date: data.date,
    reason: data.reason,
    status: data.status
  }
  return dispatch => {
    dispatch(showLoading())
    axios.post('/dayoffs', params)
      .then(function (res) {
        dispatch(addDayOffs(res.data))
        toastSuccess("Successful!")
      })
      .catch(function (error) {
        console.log(error)
        toastError("Fail!")
      })
      .finally(function () {
        dispatch(hideLoading())
      });
  }
}
