import React, { useState } from 'react';
import { Field, Form, Formik } from "formik";
import * as Yup from 'yup';
import { ReactstrapInput, ReactstrapSelect } from "reactstrap-formik";
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import {
  Col, Container, Row,
  Button, Modal, ModalHeader, ModalBody, ModalFooter
} from "reactstrap";
import * as actions from '../../../actions/account';
import * as options from './selectOptions';
import { commonValidationSchema } from './validate';
import _ from 'lodash';

const AccountForm = (props) => {
  const {
    actions,
    buttonLabel,
    title,
    item,
    id
  } = props;

  const [modal, setModal] = useState(false);
  const toggle = () => setModal(!modal);

  const specifiedValidationSchema = item ? {
    code: Yup.string().matches(/^B[0-9]{6}$/, "Starts with letter B and ends with 6 digits").required()
  } : {
      password: Yup.string().min(6).max(15).required()
    }

  const validationSchema = Yup.object({
    ...commonValidationSchema,
    ...specifiedValidationSchema
  })

  const initialValues = item ? {
    ...item,
    status: item.status ? "active" : "deactive",
    roles: item.roles[0].name,
    name: item.profile.name,
    phone_number: item.profile.phone_number,
    address: item.profile.address,
    sex: item.profile.sex,
    date_of_birth: item.profile.date_of_birth
  } : {}

  return (
    <div style={{ display: "inline-block" }}>
      <Button color="info" onClick={toggle}>{buttonLabel}</Button>
      <Modal isOpen={modal} toggle={toggle}>
        <Formik
          initialValues={initialValues}
          validationSchema={validationSchema}
          onSubmit={
            (values, { setErrors, setSubmitting }) => {
              const status = values.status === "active"
              const data = { ...values, status }
              if (item === undefined) {
                actions.addNewAccountRequest(data, setErrors, setSubmitting, toggle)
              }
              else {
                actions.updateAccountRequest(id, data, setErrors, setSubmitting, toggle)
              }
            }
          }
        >
          {({ handleSubmit, isSubmitting, values, errors }) => {
            return <Form onSubmit={handleSubmit} >
              <ModalHeader toggle={toggle}>{title}</ModalHeader>
              <ModalBody>
                <Row>
                  <Col xs="6">
                    <Field
                      label="Name"
                      name="name"
                      component={ReactstrapInput}
                    />
                  </Col>
                  <Col xs="6">
                    <Field
                      label="Phone Number"
                      name="phone_number"
                      component={ReactstrapInput}
                    />
                  </Col>
                </Row>

                <Row>
                  <Col xs="6">
                    <Field
                      label="Address"
                      name="address"
                      component={ReactstrapInput}
                    />
                  </Col>
                  <Col xs="6">
                    <Field
                      type="date"
                      label="Date of birth"
                      name="date_of_birth"
                      component={ReactstrapInput}
                    />
                  </Col>
                </Row>

                <Row>
                  <Col xs="6">
                    <Field
                      label="Email"
                      name="email"
                      component={ReactstrapInput}
                    />
                  </Col>
                  <Col xs="6">
                    <Field
                      label="Id card"
                      name="id_card"
                      component={ReactstrapInput}
                    />
                  </Col>
                </Row>

                <Row>
                  <Col xs="6">
                    <Field
                      type="date"
                      label="Joining date"
                      name="joining_date"
                      component={ReactstrapInput}
                    />
                  </Col>
                  <Col xs="6">
                    <Field
                      type="date"
                      label="Official date"
                      name="official_date"
                      component={ReactstrapInput}
                    />
                  </Col>
                </Row>

                <Row>
                  <Col xs="6">
                    <Field
                      label="Contract type"
                      name="contract_type"
                      component={ReactstrapSelect}
                      inputprops={{
                        name: "contract_type",
                        options: options.contract_type,
                        defaultOption: "Select"
                      }}
                    />
                  </Col>
                  <Col xs="6">
                    <Field
                      label="Position"
                      name="position"
                      component={ReactstrapSelect}
                      inputprops={{
                        name: "position",
                        id: "position",
                        options: options.position,
                        defaultOption: "Select"
                      }}
                    />
                  </Col>
                </Row>

                <Row>
                  <Col xs="6">
                    <Field
                      label="Status"
                      name="status"
                      component={ReactstrapSelect}
                      inputprops={{
                        name: "status",
                        id: "status",
                        options: options.status,
                        defaultOption: "Select"
                      }}
                    />
                  </Col>
                  <Col xs="6">
                    <Field
                      label="Gender"
                      name="sex"
                      component={ReactstrapSelect}
                      inputprops={{
                        name: "sex",
                        id: "sex",
                        options: options.sex,
                        defaultOption: "Select"
                      }}
                    />
                  </Col>
                </Row>

                <Row>
                  <Col xs="6">
                    <Field
                      label="Roles"
                      name="roles"
                      component={ReactstrapSelect}
                      inputprops={{
                        name: "roles",
                        id: "roles",
                        options: options.roles,
                        defaultOption: "Select"
                      }}
                    />
                  </Col>
                  <Col xs="6">
                    {item ?
                      <Field
                        label="Code"
                        name="code"
                        component={ReactstrapInput}
                      /> :
                      <Field
                        label="Password"
                        name="password"
                        component={ReactstrapInput}
                      />
                    }
                  </Col>
                </Row>
              </ModalBody>
              <ModalFooter>
                <Button color="primary" onClick={toggle}>Cancel</Button>{' '}
                <Button color="secondary" type="submit">Submit</Button>
              </ModalFooter>
            </Form>
          }}
        </Formik>
      </Modal>
    </div >
  )
}

const mapStateToProps = state => {
  return {

  };
};

const mapDispatchToProps = dispatch => {
  return {
    actions: bindActionCreators(actions, dispatch),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(AccountForm)
