const validate = value => {
  const errors = {}
  const { Reason } = value
  if (!Reason) {
    errors.Reason = 'vui lòng nhập lý do'
  } else if (Reason.length < 5 || !/^.*[a-zA-Z]+.*$/.test(Reason)) {
    errors.Reason = 'lý do phải từ 5 kí tự và không được toàn kí tự đặc biệt hoặc số'
  } else if (Reason.length > 200 || !/^.*[a-zA-Z]+.*$/.test(Reason)) {
    errors.Reason = 'lý do phải ít hơn 200 kí tự và không được toàn kí tự đặc biệt hoặc số'
  }
  return errors
}

export default validate
